INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('822b9547-abf0-4c26-8a69-5182f0cff9db', '2024-04-12T15:29:31.590Z', 'StellarApp', 'https://www.stellarapp.io/', 'images/stellarapp.webp', 'images/stellarapp-icon.webp', '{"what":"StellarApp is an AI-powered tool that simplifies and improves the goal-setting and tracking process for organizations.","who":"StellarApp is designed for businesses and organizations looking to streamline their goal-setting process and improve collaboration and visibility across teams.","best_features":["Intelligent, assisted goal cascading using AI to propose and write cascading goals for every department.","Automated tracking and smart prompts to gain confidence in the progress of critical work.","Data-driven health summaries to provide insightful digests and summaries of goal performance."],"pricing":[]}', 'Stellar is an AI-powered tool that simplifies and improves the goal-setting and tracking process for organizations. It offers intelligent, assisted goal cascading by using AI to propose and write cascading goals for every department, enabling organizations to focus and deliver on their top-line goals effectively. The tool also provides automated tracking, smart prompts, and data-driven health summaries to help teams track progress and identify areas for improvement.', NULL, '["AI Solutions","Productivity Tools","Project Management"]');
