INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c2734300-391c-49b4-9585-cd12188f44f1', '2024-04-12T15:29:31.597Z', 'StorylineJournal', 'https://www.storylinejournal.com/', 'images/storylinejournal.webp', 'images/storylinejournal-icon.webp', '{"what":"StorylineJournal is an AI-powered tool designed to simplify and enhance the journaling experience, fostering mindfulness and reducing stress for users.","who":"StorylineJournal is for individuals looking to streamline their journaling process, receive creative suggestions, and maintain privacy while chronicling their thoughts.","best_features":["AI-powered suggestions for enhanced journaling experience","Fast and encrypted services for secure privacy","Customizable journaling experience catering to a broad range of preferences"],"pricing":""}', 'StorylineJournal is an innovative AI-powered tool that aims to make journaling more efficient and enjoyable. It offers creative suggestions, fast and secure services, and a customizable experience for users. The tool has received positive feedback for its user-friendly interface, effectiveness in streamlining the journaling process, and ability to generate more content in less time. Additionally, StorylineJournal provides users with the option to generate PDFs in a real book-like format, creating a tangible artifact of their reflections.', NULL, '["AI Tools","Writing Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ff083f07-5132-40ff-ac09-6d12f4ff7339', '2024-04-12T15:29:31.597Z', 'Storymage', 'https://www.storymage.ai/', 'images/storymage.webp', 'images/storymage-icon.webp', '{"what":"Story Mage is an AI-powered tool designed to assist podcasters, agencies, VCs, and coaches in transforming audio recordings, conversations, and interviews into written content for LinkedIn and blog posts.","who":"Story Mage is for founders, consultants, and growth teams who need a faster way to generate 30 days of content for LinkedIn and improve their thought leadership.","best_features":["AI-powered tool for transforming audio recordings into written content","Proprietary AI technology for content generation and conversion","Utilization of a Stanford writer for high-quality content production"]}', 'Story Mage is an AI-powered tool that helps professionals convert audio recordings, conversations, and interviews into written content for LinkedIn and blog posts. The platform utilizes proprietary AI technology and a Stanford writer to ensure high-quality content production. It is designed to assist podcasters, agencies, VCs, and coaches in generating more inbound leads and increasing visibility through consistent daily posting.', NULL, '["AI Tools","Marketing","Writing Assistance"]');
