INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('5d1c28b8-e208-4dd5-9274-3c21db008811', '2024-04-12T15:29:31.597Z', 'StoryScape', 'https://storyscapeai.app/', 'https://media.theresanaiforthat.com/storyscape.png', 'images/storyscape-icon.webp', '{"what":"StoryScape is an innovative storytelling app that utilizes advanced AI technology to provide users with personalized and interactive reading experiences.","who":"StoryScape is designed for individuals who enjoy immersive storytelling experiences and want to engage with AI-generated content in a creative and interactive way.","best_features":["Fully personalized and interactive storytelling experience","Multiple story options with different plot twists and endings","Ability for users to make choices that shape the outcome of the story"],"pricing":[]}', 'StoryScape AI is an innovative storytelling app that uses cutting-edge AI technology to deliver personalized and interactive reading experiences. Users can choose from a variety of AI-crafted stories, make choices that affect the outcome, and explore different settings and characters. With multiple plot twists and endings, StoryScape offers a unique and engaging storytelling experience.', NULL, '["AI Tools","Story Generation"]');
