INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e9cf3f57-9ef5-41d5-812e-b7eb0eb88e43', '2024-04-12T15:29:31.600Z', 'Studis', 'https://www.studis.co.uk/', 'images/studis.webp', 'images/studis-icon.webp', '{"what":"Studis is a comprehensive study companion platform designed to facilitate academic success for students by offering a range of features and tools to streamline studying, boost motivation, and provide expert guidance.","who":"Studis is for students looking to enhance their academic performance, manage their time efficiently, set goals, seek mentorship, and connect with a supportive student community.","best_features":["Study Tracker: Navigate study progress with insightful monitoring and personalized guidance.","Task Manager: Prioritize tasks, manage time effectively, and reduce stress for stress-free studying.","Student Community: Connect, collaborate, and excel together in a supportive network."]}', 'Studis is a comprehensive study companion platform designed to streamline studying, boost motivation, and provide expert guidance for students. It offers features such as study tracking, goal setting, task management, student community, and a Pomodoro Timer to enhance the learning experience. Studis aims to empower students to take ownership of their academic journey, stay on track with their goals, and excel in a supportive environment.', NULL, '["Study Companion","Academic Success","Student Community"]');
