INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('23c62e4d-08a9-499b-b274-6f8ced6a23ef', '2024-04-12T15:29:31.600Z', 'Study Pack', 'https://www.studypack.co/', 'images/study-pack.webp', 'images/study-pack-icon.webp', '{"what":"Study Pack is an AI-powered learning assistant designed to optimize students'' comprehension and retention of educational content through tailored summaries, study guides, interactive quizzes, and collaborative learning features.","who":"Study Pack is for students of all ages and educational levels who want to enhance their learning experience, improve study efficiency, and achieve academic success.","best_features":["Tailored study guides and quizzes for personalized learning","Interactive flashcards for quick self-assessment and on-the-go learning","Innovative AI-driven note summarization and export features for efficient study management"],"pricing":[]}', 'Study Pack is an AI-powered learning assistant that helps students optimize their comprehension and retention of educational content. It offers tailored study guides, interactive quizzes, collaborative learning tools, and innovative note summarization features. Students can engage in virtual reading groups, create comprehensive study guides, and organize key concepts efficiently. With Study Pack, students can improve their study efficiency, boost productivity, and enhance their academic performance in both traditional and online learning environments.', NULL, '["AI Assistance","Study Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('5377bb1b-9b54-4412-baf0-11e0f08be639', '2024-04-12T15:29:31.600Z', 'Study Smarter Now', 'https://studysmarternow.com/', 'images/study-smarter-now.webp', 'images/study-smarter-now-icon.webp', '{"what":"Study Smarter Now is an AI-powered platform designed to enhance study sessions and improve test-taking strategies for students.","who":"This product is for students looking to optimize their study sessions, improve test-taking skills, and achieve academic success more efficiently.","best_features":["AI tutor for contextual understanding of notes, homework, or textbooks","Generation of study guides based on homework","Comprehensive AI-generated study notes that cover essential information"]}', 'Study Smarter Now is an AI-powered platform that helps students enhance their study sessions and improve test-taking strategies. It offers features such as an AI tutor for contextual understanding, generation of study guides, and comprehensive AI-generated study notes. By focusing on key concepts and problem-solving, students can achieve academic success more efficiently. The platform also provides effective test-taking strategies, time management tips, and resources to handle various test formats. Study Smarter Now aims to make the study process more efficient and effective through AI technology.', NULL, '["AI Solutions","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('34ba679a-1bb9-412d-97fa-0a37ec92d67e', '2024-04-12T15:29:31.600Z', 'Study Squeeze', 'https://www.studysqueeze.com/', 'images/study-squeeze.webp', 'images/study-squeeze-icon.webp', '{"what":"StudySqueeze is an AI-powered learning platform that automates the conversion of files and documents into flashcards and diagrams, making the note-taking process more efficient and streamlined.","who":"StudySqueeze is designed for students, professionals, educators, and anyone looking to enhance their learning experience by simplifying the process of creating study materials.","best_features":["AI-powered conversion of files into flashcards and diagrams","Support for various formats including question and answer, multiple choice, true/false, summarization, and fill in the gap","Seamless integration with popular learning apps for easy access and review of flashcards"]}', 'StudySqueeze is an AI-powered learning platform that allows users to convert files and documents into flashcards and diagrams quickly and easily. With a focus on efficiency, StudySqueeze aims to streamline the note-taking process by automating the conversion of learning materials into various formats. Users can import files in formats such as PDF, Word, PowerPoint, JPEG, or PNG, and choose the desired format for their flashcards, including question and answer, multiple choice, true/false, summarization, and fill in the gap.', NULL, '["AI-powered learning platform","Study materials conversion","Flashcards and diagrams"]');
