INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('16197505-d488-4d4b-a43c-9a8641d4ecdf', '2024-04-12T15:29:31.606Z', 'Stylize', 'https://www.usestylize.com/', 'images/stylize.webp', 'images/stylize-icon.webp', '{"what":"Stylize is an AI tool designed to enhance, edit, and stylize photos with just one click, providing users with professional-quality results without the need for technical expertise.","who":"Stylize is for individuals looking to effortlessly transform their photos into visually appealing works of art, whether they are beginners or experienced in photo editing.","best_features":["Hassle-free experience for users to achieve professional-quality results","Wide range of styles and effects to choose from, including pixelation, futuristic themes, and painting-like effects","Gallery of examples to inspire creativity and experimentation"]}', 'Stylize is an AI tool that enables users to effortlessly enhance, edit, and stylize their photos in just one click. Users can explore various styles and effects to give their photos a unique touch, with options like pixelation, futuristic themes, and painting-like effects. The tool offers a hassle-free experience, providing professional-quality results without any technical expertise in photo editing. Stylize also offers a gallery of examples to inspire creativity and experimentation, allowing users to reimagine their photos and create visually captivating images.', NULL, '["AI Tools","Art Generation","Design"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('745475a1-a8f7-49bd-8041-ca513115cd25', '2024-04-12T15:29:31.606Z', 'Subject Line Generator', 'https://subjectlinegenerator.com/', 'images/subject-line-generator.webp', 'images/subject-line-generator-icon.webp', '{"what":"The Subject Line Generator is an AI tool designed to help users create catchy email subject lines quickly to improve email open rates.","who":"This product is for individuals and businesses involved in email marketing who want to enhance the effectiveness of their email campaigns.","best_features":["AI-powered subject line generation for quick and effective results","Integration with Newsletter Pilot for automated newsletter creation","Chrome extension for easy access and use"],"pricing":[]}', 'The Subject Line Generator, powered by Newsletter Pilot, is an AI tool that generates attention-grabbing email subject lines in seconds. It is ideal for users looking to improve their email open rates and streamline their newsletter creation process. The tool is set to launch soon on NewsletterPilot.com, with the first 200 users receiving a six-month free subscription.', NULL, '["Email Management","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('44e25042-80ec-478a-aadf-c839fbb98c2c', '2024-04-12T15:29:31.606Z', 'SubsGPT', 'https://www.subsgpt.com/', 'images/subsgpt.webp', 'images/subsgpt-icon.webp', '{"what":"SubsGPT is an AI tool designed to aid in the translation of subtitles using advanced machine learning algorithms, particularly Generative Pretraining Transformer (GPT) technology.","who":"SubsGPT is for subtitle translators, content creators, filmmakers, or anyone in need of quick, accurate, multilingual subtitle translation.","best_features":["Utilizes advanced machine learning algorithms for accurate subtitle translation.","Supports informal language translation, such as transforming informal Russian phrases into informal Portuguese.","Allows interaction via json input and output for symmetrical results."],"pricing":""}', 'SubsGPT is an AI tool that uses Generative Pretraining Transformer (GPT) technology to assist in translating subtitles. It supports informal language translation and allows users to interact with the system via json input. Users can upload subtitle files in .srt format and download the translated outputs. This tool is beneficial for subtitle translators, content creators, filmmakers, or anyone in need of quick and accurate multilingual subtitle translation.', NULL, '["Translation","Content Creation","Film Production"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b1edce70-e262-4fc8-ac9d-b389d34b63f8', '2024-04-12T15:29:31.606Z', 'Substratus', 'https://www.substratus.ai/', 'images/substratus.webp', 'images/substratus-icon.webp', '{"what":"Substratus is an AI tool designed to build, train, and deploy state-of-the-art machine learning models across different cloud providers, with a focus on Large Language Models (LLMs).","who":"Substratus is for data scientists, machine learning engineers, and businesses looking to accelerate the development and deployment of AI models without the need for complex infrastructure management.","best_features":["Quick deployment of cutting-edge open-source LLMs in minutes","Integration with Kubernetes for seamless execution across multiple environments","Fully managed LLM serving stack with autoscaling capabilities"],"pricing":[]}', 'Substratus is an AI tool that simplifies the process of building, training, and deploying machine learning models, specifically focusing on Large Language Models (LLMs). It offers quick deployment of open-source LLMs, integration with Kubernetes for seamless execution, and a fully managed LLM serving stack with autoscaling capabilities.', NULL, '["Machine Learning","Cloud Computing"]');
