INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d59b8fac-4175-4815-85b8-22812821ff83', '2024-04-12T15:29:31.607Z', 'SuggestCat', 'https://www.suggestcat.com/', 'images/suggestcat.webp', 'images/suggestcat-icon.webp', '{"what":"SuggestCat is an AI-based plug-in for modern web-based text editors that automatically detects grammatical and stylistic errors in the text and provides suggested corrections. It also offers AI-powered suggestions, transformations, and translations for selected text.","who":"SuggestCat is designed for writers, editors, content creators, and anyone who uses web-based text editors like ProseMirror and TipTap. It is especially useful for those looking to improve their writing skills and enhance the quality of their content.","best_features":["Grammar correction functionality for accurate and error-free writing","AI suggestions and transformations for enhanced text quality","Upcoming AI text tone analysis feature for improved writing style"],"pricing":""}', 'SuggestCat is an AI-based plug-in for modern web-based text editors that automatically detects and corrects grammatical and stylistic errors. Developed by Emergence Engineering, it offers AI-powered suggestions, transformations, and translations for text. Users can easily apply corrections and improve their writing with the upcoming AI text tone analysis feature. Simply include the plug-in in your preferred text editor and let SuggestCat take care of the rest.', NULL, '["Writing Assistance","AI Solutions"]');
