INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('942c3b9b-03f2-4967-87ab-67a36c4710dc', '2024-04-12T15:29:31.607Z', 'Summa', 'https://summacourse.com/', 'images/summa.webp', 'images/summa-icon.webp', '{"what":"Summa is an AI-powered educational tool designed to help students and teachers optimize study time by generating practice exams based on user-provided notes and classroom materials.","who":"Summa is beneficial for students at all levels of education looking to improve their exam skills, enhance learning comprehension, and identify areas requiring improvement.","best_features":["Transforms traditional study materials into actionable practice questions","Provides explanations alongside generated questions for better understanding","Helps students identify strengths and weaknesses in preparation for exams"],"pricing":[]}', 'Summa is an AI-powered educational tool aimed at helping students and teachers optimize study time by generating practice exams based on user-provided notes and classroom materials. It simplifies the studying process, aids in identifying areas requiring improvement, and enhances learning comprehension through interactive practice questions.', NULL, '["AI Tools","Exam Preparation","Study Aid"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('56074103-6d67-4e3b-b44d-a3bd636e8880', '2024-04-12T15:29:31.607Z', 'Summaletter', 'https://summaletter.com/', 'images/summaletter.webp', 'images/summaletter-icon.webp', '{"what":"Summaletter is an AI tool designed to condense multiple newsletters into a single, digestible summary, helping users stay updated without spending excessive time on reading. It utilizes AI algorithms to extract key points from newsletters and offers both free and Pro versions for customization.","who":"Summaletter is ideal for individuals or organizations subscribed to numerous newsletters who struggle to keep up with the influx of information. It caters to users looking for a convenient and time-saving solution to manage their subscriptions effectively.","best_features":["AI-powered summarization of newsletters for efficient reading","Support for multiple languages catering to diverse users","Free basic service with the option to upgrade to Pro for advanced features and customization"],"pricing":[{"plan":"Free","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["Condense up to 10 newsletters per month"]},{"plan":"Pro","price":9.99,"billing_cycle":"Monthly","original_price":9.99,"discount":"N/A","features":["Advanced summaries","Customizable content with set keywords"]}]}', 'Summaletter is an AI tool that simplifies the process of managing multiple newsletters by condensing them into a concise summary. It saves users time by extracting essential information from newsletters and presenting it in a streamlined format. With support for multiple languages and a free basic service, Summaletter offers a convenient solution for staying informed without feeling overwhelmed.', NULL, '["AI Tools","Summarization","Email Management"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('99df7309-98c7-4e4e-b94d-63b3a31792a1', '2024-04-12T15:29:31.607Z', 'SummarAIze', 'https://www.summaraize.com/', 'images/summaraize.webp', 'images/summaraize-icon.webp', '{"what":"SummarAIze is an AI-powered tool that helps users repurpose content from podcasts, webinars, and other video sources into various formats for social media, email, and more.","who":"SummarAIze is designed for content creators, marketers, podcasters, and companies looking to efficiently repurpose and leverage their audio and video content for marketing purposes.","best_features":["AI-generated summaries for quick content creation","Support for various audio and video formats for flexibility","Content repurposing without manual work for time-saving"]}', 'SummarAIze is an AI-powered tool that enables users to repurpose content from podcasts, webinars, and other video sources into engaging and shareable social posts, email newsletters, summaries, quotes, and more. Trusted by top podcasts, companies, and marketers, SummarAIze offers features such as social media posts, email content generation, AI-generated summaries, and more to support marketing channels and meet audience expectations. Users can easily transform audio and video content into various formats to maximize content production and reach a wider audience.', NULL, '["AI Tools","Marketing","Podcasting"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('6b2a40ff-df38-415c-9b66-47cceb3989cf', '2024-04-12T15:29:31.608Z', 'Summarify', 'https://summarify.app/', 'images/summarify.webp', 'images/summarify-icon.webp', '{"what":"Summarify AI for YouTube is an iOS app that utilizes advanced AI technology, specifically ChatGPT and OpenAI, to provide users with summarized versions of YouTube videos.","who":"Summarify is designed for users who consume a lot of video content on YouTube and are looking to save time by quickly condensing lengthy videos into concise summaries.","best_features":["Customizable summary styles tailored to user preferences.","Video timestamps linked directly to corresponding moments in the video for easy navigation.","Chapter and custom summaries for personalized content consumption."],"pricing":[]}', 'Summarify AI for YouTube is an iOS app that revolutionizes the way users consume video content by providing accurate and coherent summaries of YouTube videos. With advanced AI technology, users can quickly condense lengthy videos into concise summaries, choose from various summary styles, and easily navigate through videos using timestamps. Summarify also offers chapter and custom summaries, as well as the ability to search for videos and download transcripts in multiple formats.', NULL, '["AI Solutions","Video Editing","Text-to-Video"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('907bb657-2d14-4cf2-b38d-05ce29df5069', '2024-04-12T15:29:31.608Z', 'SummariseThis', 'https://twitter.com/summarisethis/', 'images/summarisethis.webp', 'images/summarisethis-icon.webp', '{"what":"SummariseThis is an AI tool provided by Twitter, Inc. It is a browser-based application designed to help users summarize content on platforms like Twitter. Users are advised to switch to supported browsers for optimal functionality.","who":"This product is for individuals who frequently use platforms like Twitter and need a tool to quickly summarize content. It is also suitable for businesses and professionals looking to streamline their content curation process.","best_features":["Efficient content summarization for quick information retrieval","Browser-based application for easy access on various devices","Provided by Twitter, Inc. for trusted and reliable service"]}', 'SummariseThis is a browser-based AI tool provided by Twitter, Inc. Users can easily summarize content on platforms like Twitter for quick information retrieval. It is recommended to use supported browsers for optimal performance.', NULL, '["Social Media","Content Curation"]');
