INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('eb42576c-80ae-4aba-940e-7b0e83826ad8', '2024-04-12T15:29:31.608Z', 'Summarize.one', 'https://summarize.one/en', 'images/summarize-one.webp', 'images/summarize-one-icon.webp', '{"what":"Summarize.One is an AI tool designed to provide automatic and concise summaries of both WhatsApp voice messages and text messages, regardless of their length. It aims to simplify the process of interacting with WhatsApp messages, making communication more efficient and seamless.","who":"Summarize.One is for individuals who receive lengthy voice messages on WhatsApp and want to save time by quickly getting to the main points without listening to the entire message. It is also beneficial for those who want to discreetly listen to voice messages in situations where it may not be appropriate to do so.","best_features":["Discreet listening to voice messages in various settings such as meetings, libraries, or loud environments.","Pocket Summarizer feature for neatly summarizing conversations or braindumps, accessible weeks later.","Efficient and stress-free experience by highlighting key points of messages before listening to them fully."],"pricing":[{"plan":"FREE","price":0,"billing_cycle":"monthly","original_price":0,"discount":"N/A","features":["5 Voice-/Text Summaries/mo","Adjust summarization length","Summarize Memos to yourself"]},{"plan":"PLUS","price":3.79,"billing_cycle":"monthly","original_price":3.79,"discount":"N/A","features":["Unlimited Voice-/Text Summaries","Transcriptions (Word-by-Word)","Bullet-point Summaries"]},{"plan":"PRO","price":5.99,"billing_cycle":"monthly","original_price":5.99,"discount":"N/A","features":["Fully Automatic Summary of EVERY new Voice Message","Posted directly in chat or private","Black-/Whitelist for Contacts"]}]}', 'Summarize.One is an AI tool that provides automatic and concise summaries of WhatsApp voice messages and text messages. It offers features such as discreet listening, Pocket Summarizer for neatly summarizing conversations, and efficient message highlighting. Users can save time, avoid misunderstandings, and prioritize their communication effectively.', NULL, '["AI Tool","Messaging","Productivity"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('200d261a-4f66-440b-afee-fa9047390c6a', '2024-04-12T15:29:31.608Z', 'Summarizely', 'https://www.summarizely.org/', 'images/summarizely.webp', 'images/summarizely-icon.webp', '{"what":"Summarizely is an AI tool designed to help teams stay updated on their teammates'' GitHub activities by summarizing the activities and sending them to designated Slack channels.","who":"Summarizely is for teams who want to remain updated on important GitHub activities without dedicating significant time and resources to manually monitoring activities.","best_features":["Easy setup with just a few clicks, no need for any code or credit card.","Data security - does not store any data, ensuring secure and protected information.","Powered by OpenAI''s latest API for cutting-edge summarizing features."]}', 'Summarizely is an AI tool that summarizes teammates'' GitHub activities and sends them to Slack channels. It is easy to set up, secure, and powered by OpenAI''s latest API. Teams can sign up for early access and integrate Summarizely into Slack channels to stay updated without manual monitoring.', NULL, '["Productivity Tools","Data Analysis","AI Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('4076c30d-d488-4702-bb91-5e418c959bd7', '2024-04-12T15:29:31.608Z', 'SummarizePaper', 'https://summarizepaper.com/', 'images/summarizepaper.webp', 'images/summarizepaper-icon.webp', '{"what":"SummarizePaper.com is an open-source AI tool that provides concise and understandable summaries of the latest research articles on arXiv. The tool uses natural language processing to assist users in understanding key points and offers a chatbot feature for direct communication with one of the server''s arXiv papers.","who":"This tool is for researchers, students, journalists, or anyone interested in staying informed of the latest developments in their field. It is designed to help individuals quickly grasp the main ideas of research articles without having to read through the entire text.","best_features":["Concise and understandable summaries of research articles","Chatbot feature for direct communication with arXiv papers","Search functionality by keywords, author, title, arXiv ID, and specific field codes"]}', 'SummarizePaper.com is an open-source AI tool that provides concise and understandable summaries of the latest research articles on arXiv. Users can search for articles by keywords, author, title, arXiv ID, and refine the search using specific field codes. The tool uses natural language processing to help users understand key points and offers a chatbot feature for direct communication with arXiv papers. It is a valuable resource for researchers, students, journalists, or anyone interested in staying up-to-date with the latest developments in their field.', NULL, '["AI Tools","AI Services","Artificial Intelligence"]');
