INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('fcbe060f-19b4-425b-af1a-8ad9618d144a', '2024-04-12T15:29:31.609Z', 'Summarizing Tool', 'https://www.summarizingtool.net/', 'images/summarizing-tool.webp', 'images/summarizing-tool-icon.svg', '{"what":"Summarizing Tool is an online text summarization service that uses advanced AI technology to generate concise summarizations of lengthy text while maintaining the core meaning of the original content.","who":"This tool is designed for students, teachers, journalists, and anyone who needs to summarize articles, papers, or documents quickly and efficiently.","best_features":["Advanced AI technology for accurateness and efficiency","Multilingual capabilities for diverse user needs","User-friendly interface for easy access and navigation"],"pricing":[]}', 'Summarizing Tool is an online text summarization service that uses advanced AI technology to generate concise summarizations of lengthy text. The tool is designed to maintain the core meaning of the original content while reducing the volume of text. It offers versatile summary results in general, bullet points, or ''best line'' format, and supports multiple languages for user convenience.', NULL, '["AI Tools","Content Creation","Writing Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('05946fa5-d134-4377-8df8-cb89fd220df8', '2024-04-12T15:29:31.609Z', 'SummaryGPT', 'https://t.me/summarygptenbot', 'images/summarygpt.webp', 'images/summarygpt-icon.webp', '{"what":"SummaryGPT EN is a Telegram chatbot designed to help users summarize key points of various types of content, including text, URLs, PDFs, and YouTube videos. It utilizes GPT technology for efficient summarization.","who":"This product is for anyone who needs quick and concise summaries of lengthy texts, URLs, PDFs, or YouTube videos. It is especially useful for individuals looking to save time by getting an overview of content without having to read through it entirely.","best_features":["Efficient summarization of various types of content","Powered by advanced GPT technology for accurate analysis","Accessible through Telegram for easy usage"]}', 'SummaryGPT EN is a Telegram chatbot that provides users with quick and concise summaries of text, URLs, PDFs, and YouTube videos. It is designed to streamline workflows and save time by offering essential key points of the content without the need to go through the entire material. The chatbot is powered by GPT technology, ensuring accurate and efficient summarization. Users can easily access the chatbot by searching for ''@summarygptenbot'' on Telegram or clicking on the provided link.', NULL, '["Chat Solutions","Summarization"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('bc857ca7-b35b-4b90-8ba8-d2e8e88425a9', '2024-04-12T15:29:31.610Z', 'SummaryWithAI', 'https://www.summarywithai.com/', 'images/summarywithai.webp', 'images/summarywithai-icon.webp', '{"what":"Summary with AI is an artificial intelligence tool designed to create summaries of PDF files, catering to various types of documents such as annual reports, legal documents, and case studies.","who":"This product is ideal for professionals, students, researchers, and anyone who needs to quickly extract key information from lengthy PDF documents.","best_features":["Ability to handle large PDF files up to 200 MB in size","Produces high-quality summaries that retain crucial facts from the original document","Operates on a credit-based system with 20 free credits for new users"]}', 'Summary with AI is an AI tool that simplifies the process of summarizing PDF files, allowing users to quickly extract essential information from documents like annual reports, legal papers, and case studies. With the ability to handle large files and produce high-quality summaries, this tool is perfect for professionals and students looking to save time and improve productivity.', NULL, '["AI Tools","Document Management"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ace131ea-9023-4e67-b03d-5709bf818eba', '2024-04-12T15:29:31.610Z', 'Summon', 'https://www.figma.com/community/plugin/1172891596048319817', 'images/summon.webp', 'images/summon-icon.webp', '{"what":"Summon.ai is a free and open-source design tool powered by Dall-E-2 AI technology. It allows users to generate, edit, and create variant images with advanced AI features.","who":"This product is ideal for designers, artists, content creators, and anyone looking to enhance their image editing and creation capabilities using AI technology.","best_features":["Advanced AI image generation models for better design outcomes","Ghost artist AI draw assist for creative assistance","Save image feature to discover user-generated and shared content worldwide"],"pricing":[]}', 'Summon.ai is a cutting-edge design tool that leverages Dall-E-2 AI power to provide users with enhanced image editing and creation capabilities. With features like ghost artist AI draw assist and save image functionality, users can explore new creative possibilities and access user-generated content globally.', NULL, '["Design","Image Editing","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a5af4e6e-6d10-4e58-b2d5-50f106964222', '2024-04-12T15:29:31.610Z', 'SummrAIz', 'https://summraiz.fyi/', 'images/summraiz.webp', 'images/summraiz-icon.webp', '{"what":"SummrAIz is an AI-powered newsletter summarization tool that condenses multiple newsletters into a single consolidated newsletter, delivering the most important news from the user''s favorite sources directly to their inbox twice a week.","who":"SummrAIz is designed for individuals who receive multiple newsletters in their email and want to stay informed without feeling overwhelmed by the abundance of information.","best_features":["Summarizes multiple newsletters into a single consolidated newsletter","Delivers the most important news from favorite sources directly to the user''s inbox twice a week","Saves time and helps users stay up-to-date on the news that matters to them"]}', 'SummrAIz is an AI-powered newsletter summarization tool that helps users stay informed without feeling overwhelmed by the abundance of newsletters in their email. The tool delivers a consolidated newsletter containing the most important news from the user''s favorite sources directly to their inbox twice a week. With SummrAIz, users can save time and avoid the need to read multiple newsletters as the tool condenses the information into a manageable 250-word summary.', NULL, '[""]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d12e9770-31c1-4bfc-a5db-3323388b872a', '2024-04-12T15:29:31.610Z', 'SumsItUp', 'https://sumsitup.ai/', 'images/sumsitup.webp', 'images/sumsitup-icon.webp', '{"what":"SumsItUp is an AI-powered application that provides users with custom summaries and translations of online content in various formats and styles. It offers users complete control over the summary generation process, allowing them to tailor the summaries to their specific needs.","who":"SumsItUp is designed for a wide range of users, including teachers looking to engage students with current events, analysts who need to review financial reports efficiently, privacy advocates decoding complex privacy policies, social media influencers creating engaging posts, digital designers simplifying technical articles, and busy individuals who want to catch up on reading through summaries.","best_features":["Customizable summaries in various formats and styles","Ability to use custom modifiers for specific focus areas","Integration with multiple applications for easy content sharing"]}', 'SumsItUp is an AI-based application that enables users to abridge and reformulate online content according to their requirements. It offers custom summaries in various formats and styles, with the option to use custom modifiers for specific focus areas. Users can share content to SumsItUp from their preferred applications and receive summaries that can be read aloud by the AI. The tool is versatile and can be used for educational purposes, financial report analysis, social media content creation, technical article translations, and more.', NULL, '["AI Tools","Content Creation","Translation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7b24bbfa-bfa9-4ed7-bf90-83a145d7de3d', '2024-04-12T15:29:31.610Z', 'Sune', 'https://suneai.com/', 'images/sune.webp', 'images/sune-icon.webp', '{"what":"Sune is an AI tool designed to create a collaborative workspace for users to bring their documents, projects, and tasks to life in a new way. It offers an infinite canvas, functioning as a virtual whiteboard, where users can visually organize their work in a way that makes sense to them.","who":"Sune is designed for teams and individuals looking to streamline their workflow, enhance collaboration, and automate tasks in a dynamic workspace environment.","best_features":["Infinite canvas for visual organization of work","Ability to create and edit blocks within the workspace","Automation capabilities for scaling work and enhancing productivity"]}', 'Sune is an AI tool that provides a collaborative workspace where users can bring their documents, projects, and tasks to life in a visually organized manner. It offers features such as an infinite canvas, block creation and editing, and automation capabilities to streamline work processes and enhance productivity. Sune aims to act as a teammate in the creative process, fostering collaboration and customization in the workspace.', NULL, '["Collaboration","Automation","Productivity"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b2f99c20-94a7-4255-a21c-e42727bd615f', '2024-04-12T15:29:31.610Z', 'Sunflower Sparrow', 'https://sunflower.industries/', 'images/sunflower-sparrow.webp', 'images/sunflower-sparrow-icon.webp', '{"what":"Sunflower Sparrow is an AI tool developed by Sunflower Industries that transforms vocals into AI voices in a Digital Audio Workstation with near-real time playback.","who":"This product is designed for musicians, audio engineers, content creators, and anyone looking to experiment with voice manipulation and creation in their music or audio projects.","best_features":["Unlimited voice conversions for expansive use","Royalty-free conversions cleared for commercial use","Ability to load custom models for unique voice creations"],"pricing":[]}', 'Sunflower Sparrow is an AI tool by Sunflower Industries that transforms vocals into AI voices in a Digital Audio Workstation with near-real time playback. It offers unlimited voice conversions, royalty-free for commercial use, and allows users to load custom models for unique voice creations. The tool supports VST & AU plugins, is available for download on M1 Macs, and plans to extend support to Windows in the future. Sunflower Sparrow aims to enable new modes of musical expression while promoting ethical usage.', NULL, '["AI Tools","Audio Editing","Artificial Intelligence"]');
