INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3863f604-cbd2-413e-8b12-0ee8080f2434', '2024-04-12T15:29:31.620Z', 'Swell AI', 'https://swellai.com', 'images/swell-ai.webp', 'images/swell-ai-icon.webp', '{"what":"Swell AI is an AI tool designed for podcasters and content creators to generate written content such as show notes, articles, social media posts, and transcripts. It also offers SEO optimization and content ranking features.","who":"Swell AI is for podcasters, content creators, businesses, and professionals looking to streamline their content creation process and improve their online presence.","best_features":["AI-powered content generation for show notes, articles, and social media posts","Built-in SEO optimization and content ranking features","Dashboard for managing multiple podcasts and integrations with various platforms"]}', 'Swell AI is an AI tool for podcasters and content creators that generates written content such as podcast show notes, articles, social media posts, and transcripts. It also offers built-in SEO optimization and content ranking features. The tool includes a dashboard for managing multiple podcasts and integrations with various platforms such as Apple Podcasts, Google Drive, and Dropbox.', NULL, '["Podcasting","AI Tools","SEO"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c841fc07-de66-4755-be7b-97a6177b9014', '2024-04-12T15:29:31.620Z', 'Swif', 'https://www.swif.ai/', 'images/swif.webp', 'images/swif-icon.webp', '{"what":"Swif is an AI-powered platform designed for unified device management and compliance automation, catering to organizations'' technical, cybersecurity, and HR teams to enhance operational efficiency and security.","who":"Swif is ideal for organizations looking to streamline their device management processes, ensure compliance across all devices, and simplify onboarding and offboarding procedures.","best_features":["Device Security: Swif offers robust device security measures to protect company assets and ensure information privacy.","Access Management: The platform enables efficient management of employee access and permissions across various devices.","Compliance Automation: Swif helps automate device security and access reports to meet compliance requirements."],"pricing":[]}', 'Swif is an AI-powered platform designed for unified device management and compliance automation. It offers solutions for technical, cybersecurity, and HR teams, making operations more efficient and secure. The platform''s main capabilities include device security, access management, and IT disposition, with a unique smart group feature for configuring rules and auto-enrolling device policies. Swif ensures device compliance across all operating systems and integrates with HR systems for simplified onboarding and offboarding processes. It also offers seamless integration with Vanta, Drata, and Secureframe for SOC2 certification.', NULL, '["Compliance Automation","Device Management","HR Integration"]');
