INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('62d9d765-78ff-4033-af3d-de9c313600e6', '2024-04-12T15:29:31.622Z', 'Swork', 'https://swork.app/', 'images/swork.webp', 'images/swork-icon.webp', '{"what":"Swork is an AI-powered jobhunt assistant designed to revolutionize the way job seekers find employment opportunities.","who":"Swork is for job seekers looking to streamline their job hunting process, minimize bias, and secure better job opportunities.","best_features":["Revolutionary job matching based on skills and personality","Direct application to desired positions within the app","Powered by cutting-edge AI technology"],"pricing":""}', 'Swork is an AI-powered jobhunt assistant that evaluates both the skills and personality of the user, as well as the requirements of available jobs to provide efficient and unbiased job matching. Users can browse through job listings, swipe to indicate interest, and apply directly to desired positions. Swork aims to accelerate the job hunting process, minimize bias, and secure better job opportunities for users. Developed by UnderstandLing, Swork is available for download on Android and iOS.', NULL, '["Job Searching","AI Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2c039e69-4b0d-4d7a-b932-f730d64912c2', '2024-04-12T15:29:31.622Z', 'SWOT Analysis', 'https://swotanalysis.dev/', 'images/swot-analysis.webp', 'images/swot-analysis-icon.webp', '{"what":"SWOTAnalysis.dev is an AI tool designed to generate SWOT analyses for companies based on their descriptions. It utilizes AI technology to evaluate the strengths, weaknesses, opportunities, and threats of an organization or project, providing valuable insights for strategic planning.","who":"This product is ideal for business owners, project managers, consultants, and anyone involved in strategic decision-making processes. It can benefit startups looking to assess their competitive position, established companies seeking growth opportunities, and individuals interested in analyzing various aspects of a business.","best_features":["AI-powered technology for accurate and efficient SWOT analysis generation.","Comprehensive evaluation of internal and external factors impacting the organization.","Ability to download the generated SWOT analysis as an image for easy sharing and reference."],"pricing":""}', 'SWOTAnalysis.dev is an AI tool that automates the process of creating SWOT analyses for companies. By analyzing the strengths, weaknesses, opportunities, and threats of an organization or project, this tool provides valuable insights for strategic planning and decision-making. Users can input a company description and receive a detailed SWOT analysis generated by AI technology. The tool helps identify key factors influencing the success of a business and offers a comprehensive understanding of its current state and potential growth challenges.', NULL, '["Business Tools","AI Services","Data Analysis"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7fcb8a87-3b84-4ce5-8981-7f878273f540', '2024-04-12T15:29:31.623Z', 'Swyft', 'https://www.swyftai.com/', 'images/swyft.webp', 'images/swyft-icon.svg', '{"what":"Swyft AI is an automated tool designed for Sales teams to update CRM fields using Artificial Intelligence, saving time and improving data hygiene.","who":"Swyft AI is for Sales teams of all sizes looking to automate CRM updates and streamline sales processes.","best_features":["Automatic note-taking during sales calls","Seamless integration with Salesforce for updating CRM fields","Structured data and context feature for discussing next steps"]}', 'Swyft AI is an automated tool for Sales teams to update CRM fields using Artificial Intelligence. It saves time by automatically taking notes during sales calls, summarizing important information, and generating next steps. The tool seamlessly integrates with Salesforce to update relevant CRM fields instantly, improving data hygiene and efficiency. Swyft AI offers three different plans tailored to the specific needs of Sales teams, along with a free Beta program, excellent customer support, and positive testimonials from users.', NULL, '["AI Assistance","CRM Integration","Productivity Tools"]');
