INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f9cc7ea9-18b9-46fa-9915-cc3633a35dc9', '2024-04-12T15:29:31.624Z', 'Symphony.run', 'https://www.symphony.run/', 'images/symphony-run.webp', 'images/symphony-run-icon.webp', '{"what":"Symphony.run is a tool that enables developers to create conversational programs that can be interacted with through spoken language. It provides natural language processing capabilities and integration with platforms like Discord.","who":"Symphony.run is designed for developers and programmers who want to build applications that can process and respond to verbal commands and requests. It is also suitable for those looking to integrate conversational programs with popular communication platforms like Discord.","best_features":["Comprehensive documentation for guidance and instructions on utilizing the tool''s capabilities.","Integration with Discord for seamless interaction with the Discord community.","GitHub repository for code sharing, collaboration, and staying up-to-date with the latest updates and enhancements."],"pricing":[]}', 'Symphony.run is a tool that facilitates the creation of conversational programs by supporting natural language processing and providing resources for effective implementation. It allows developers to build applications that can be interacted with through spoken language, with integration options for platforms like Discord.', NULL, '["AI Tools","Chat Solutions","Coding Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('16c00b69-44ba-428e-8c01-40224b26f90a', '2024-04-12T15:29:31.624Z', 'Synapse Tutor', 'https://synapsetutor.com/', 'images/synapse-tutor.webp', 'images/synapse-tutor-icon.webp', '{"what":"Synapse Tutor is an educational AI tool designed to provide a personalized and interactive learning experience focused on mathematics.","who":"Synapse Tutor is for individuals who prefer an active learning approach and value personalized educational resources.","best_features":["Personalized and interactive learning experience","User-customized approach tailored to individual student needs","Move beyond passive watching to a more engaging form of learning"],"pricing":""}', 'Synapse Tutor is an educational AI tool that goes beyond traditional video courses by offering a personalized and interactive learning experience focused on mathematics. It tailors the learning experience to the specific needs of each student, providing a more engaging form of learning compared to passive watching. Users must have JavaScript enabled to fully utilize its features.', NULL, '["AI Tools","Mathematics","Personalized Learning"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9b18690e-a11e-419a-834d-f520c61e82af', '2024-04-12T15:29:31.624Z', 'Synapso AI', 'https://synapso.ai/', 'images/synapso-ai.webp', 'images/synapso-ai-icon.webp', '{"what":"Synapso.AI is an AI-driven tool designed to streamline content creation for business marketing efforts by generating high-quality copy that engages audiences and promotes conversion.","who":"This tool is for marketers, businesses, entrepreneurs, and agencies looking to enhance their digital marketing strategy and create compelling content efficiently.","best_features":["Access to over 16+ premium content generators","Pre-built premium use-cases for diverse industry topics","AI Assistant for personalized content ideas and marketing strategies"],"pricing":[{"plan":"PRO Plan","price":0,"billing_cycle":"Free","original_price":0,"discount":"N/A","features":["Access to over 16+ content generators","AI Assistant for personalized content ideas","Advanced AI-powered writing assistant tool"]}]}', 'Synapso.AI is an AI-driven tool that streamlines content creation for marketing efforts. It offers features like social media content creation, blog article generation, product descriptions, and SEO-friendly keyword content. The platform also provides automated response generation based on customer reviews and a PRO Plan with access to 16+ content generators and a personal AI assistant for tailored marketing strategies.', NULL, '["Content Creation","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d7d4068d-db96-435b-97e2-adcb4e13a09a', '2024-04-12T15:29:31.624Z', 'Synced', 'https://synced.so/', 'images/synced.webp', 'images/synced-icon.svg', '{"what":"Synced is an AI tool that allows users to easily create and embed a Help Center on their website using Notion, a content creation platform. With Synced, users can enhance their existing workflow in Notion by writing and managing help documentation directly within the platform.","who":"Synced is for businesses and organizations looking to provide comprehensive support to their users by creating and embedding a Help Center on their website using Notion.","best_features":["Automatic generation of Help Center site and widget based on content in Notion","Contextual help within the application itself for users to access relevant information","Customization options to match the branding of the user''s website"]}', 'Synced is an AI tool that streamlines the process of creating and embedding a Help Center on a website using Notion. It offers features such as automatic generation of Help Center site and widget, contextual help within the application, and customization options for branding consistency.', NULL, '["Content Creation","Customer Support","SEO"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('883b613a-4318-44f1-95e7-e81b22c0e068', '2024-04-12T15:29:31.624Z', 'Syncly', 'https://syncly.app/', 'images/syncly.webp', 'images/syncly-icon.webp', '{"what":"Syncly is an AI-powered feedback analysis tool designed to help businesses centralize and categorize customer feedback to enhance customer experience.","who":"Syncly is ideal for businesses looking to improve their customer service and gain valuable insights from customer feedback.","best_features":["Dynamic Sentiment Analysis for monitoring customer sentiment trends","Fluid Taxonomy for automated tagging and categorization of feedback","Auto Prioritization for identifying top revenue-influencing issues"],"pricing":[]}', 'Syncly employs advanced artificial intelligence technology to analyze and categorize customer feedback swiftly, revealing hidden negative sentiments that can affect customer experience. This tool provides a robust platform that centralizes all your customer feedback across various channels, helping you take proactive measures based on real-time insights to enhance your overall customer experience.', NULL, '["Feedback Analysis","AI Tools","Customer Experience"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('6bc40204-1704-42a9-94bc-9b497b3caae8', '2024-04-12T15:29:31.625Z', 'SynthaceGPT', 'https://www.synthace.com/', 'images/synthacegpt.webp', 'images/synthacegpt-icon.svg', '{"what":"The Synthace Experiment Platform is a digital experiment platform designed for R&D teams in the life science industry, enabling them to design and execute powerful experiments in their lab and automatically generate structured datasets.","who":"This product is specifically for R&D teams in the life science industry who want to conduct experiments efficiently, gather structured data automatically, and gain valuable insights to drive innovation and accelerate research and development processes.","best_features":["Eliminates the need for coding, making it accessible to users with no programming background.","Allows researchers to easily design experiments tailored to their specific needs and goals.","Automatically generates structured data from experiment results, streamlining the data collection process and saving valuable time."],"pricing":[]}', 'The Synthace Experiment Platform is a digital experiment platform for R&D teams in the life science industry, enabling them to design and execute powerful experiments in their lab and automatically generate structured datasets. It eliminates the need for coding, making it accessible to users with no programming background. Researchers can easily design experiments tailored to their needs, run them in their laboratory, and the platform generates structured data from the results automatically.', NULL, '["Data Analysis","Bioprocess Development","Molecular Biology"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2b7ee32d-2bd9-4b37-905f-f32790bff0e2', '2024-04-12T15:29:31.625Z', 'Synthesis Tutor', 'https://www.synthesis.com/tutor', 'images/synthesis-tutor.webp', 'images/synthesis-tutor-icon.webp', '{"what":"Synthesis Tutor is an AI-powered educational tool designed to provide personalized math tutoring for children. It incorporates modern AI tools to create an engaging and effective learning experience.","who":"Synthesis Tutor is for parents looking for a comprehensive and personalized math tutoring solution for their children aged 7-11.","best_features":["Tailored feedback and exercises to uncover the deep structure of math","Encourages critical thinking and fosters a love for math","Affordable pricing at $29 per month with a 30-day money-back guarantee"],"pricing":[{"plan":"Monthly Subscription","price":29,"billing_cycle":"per month","original_price":29,"discount":"","features":["Delightful exercises for deep math understanding","Tailored feedback for each child","30-day money-back guarantee"]}]}', 'Synthesis Tutor is an AI-powered educational tool developed by a team funded by DARPA, offering personalized math tutoring for children aged 7-11. It covers a wide range of math topics and aims to create a sense of magic and excitement to foster a love for math. The tool is user-friendly, offers a patient and engaging teaching style, and emphasizes privacy and compliance with COPPA regulations.', NULL, '["Mathematics","AI Tools","Children"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1daf83c9-0046-4341-9268-d1d41bc2b435', '2024-04-12T15:29:31.625Z', 'Synthesis Youtube', 'https://home.thesynthesis.app/', 'images/synthesis-youtube.webp', 'images/synthesis-youtube-icon.webp', '{"what":"Synthesis Youtube is an AI tool designed to help learners easily find relevant video segments from podcast channels. It streamlines the process of searching for specific topics within podcasts, enhancing learning and knowledge acquisition.","who":"This product is ideal for students, professionals, and anyone looking to efficiently access valuable information from podcast channels without having to listen to entire episodes. It caters to individuals seeking to optimize their learning experience through curated video segments.","best_features":["AI-powered search functionality for precise topic retrieval","Customizable selection of podcast channels for tailored results","Enhanced learning and knowledge acquisition through curated video segments"],"pricing":[]}', 'Synthesis Youtube is an innovative AI tool that simplifies the process of finding specific video segments within podcast channels. By entering queries and selecting preferred podcast channels, users can access curated video content on topics such as cold exposure, happiness, morning routines, time management, and decision making. This tool revolutionizes the way individuals consume educational content, offering a more efficient and personalized learning experience.', NULL, '["Education","Content Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a0e67457-6d82-4b71-8892-26349335d8af', '2024-04-12T15:29:31.625Z', 'Synthesis', 'https://synthesis.is', 'images/synthesis.webp', 'images/synthesis-icon.webp', '{"what":"Synthesis Kids Think is an AI-powered tutoring tool designed to enhance problem-solving skills in children aged 7 and above through collaborative games and activities.","who":"This product is for parents looking to help their children develop critical thinking and communication skills, as well as for educators seeking innovative tools to enhance student learning.","best_features":["AI-powered tutoring tool","Collaborative games for problem-solving","Developed in partnership with DARPA researchers"],"pricing":[]}', 'Synthesis Kids Think is an innovative AI-powered tutoring tool designed to enhance problem-solving skills in children aged 7 and above. Developed in partnership with DARPA researchers, this tool offers collaborative games that foster communication and critical thinking abilities. Created within SpaceX''s experimental school, Synthesis aims to cultivate students'' strategic thinking and collaborative problem-solving capabilities. With a focus on empowering young minds to tackle complex challenges, Synthesis provides a superhuman math tutoring experience that is changing the landscape of education.', NULL, '["AI-powered tutoring","Problem-solving skills","Collaborative learning"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('68a3c2c0-36c1-41f9-919a-5ec03438190b', '2024-04-12T15:29:31.625Z', 'Syntheticusers', 'https://www.syntheticusers.com/', 'images/syntheticusers.webp', 'images/syntheticusers-icon.webp', '{"what":"Synthetic Users is a user research platform that simplifies the process through AI-driven participants, providing valuable insights for businesses to improve their products.","who":"This product is ideal for businesses looking to streamline their user testing process and gain valuable insights to enhance their products.","best_features":["AI-driven participants for user research","Seamless onboarding process","30-day free trial for new users"]}', 'Synthetic Users offers AI-driven participants for user research, providing valuable insights to help businesses improve their products. With a seamless onboarding process and a 30-day free trial, it streamlines the user testing experience and offers cost-effective solutions.', NULL, '["User Research","AI-driven Participants","Product Improvement"]');
