INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('05607add-e9f8-4c08-863b-ad4bb6513a23', '2024-04-12T15:29:31.626Z', 'SynVision AI', 'https://synvision.ai/', 'images/synvision-ai.webp', 'images/synvision-ai-icon.webp', '{"what":"SynVision AI is an AI tool designed to create Virtual Assistants using your data library. It enables users to quickly and easily tailor AI virtual characters to reflect their brand or project requirements, with no prior coding knowledge required.","who":"SynVision AI is for businesses looking to transform their operations and enrich customer interactions via AI technology. It is suited for those who desire to implement AI chatbot technology into their operations with a simple setup process and wide-ranging platform compatibility.","best_features":["Ability to create unique AI virtual characters with tailored personalities","Integration across various platforms and languages","Privacy and data security with securely stored user uploaded files and URLs"]}', 'SynVision AI is an AI tool that allows businesses to easily create AI virtual assistants tailored to their brand or project requirements. It offers a user-friendly solution for deploying AI chatbots, enabling businesses to provide 24/7 customer assistance. With a focus on privacy and data security, SynVision AI keeps all user uploaded files and URLs securely stored. This product is ideal for businesses seeking to implement AI chatbot technology into their operations without the need for coding knowledge.', NULL, '["Chat Solutions","Business Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('cf27f147-06f2-4111-986c-eea088b664d0', '2024-04-12T15:29:31.626Z', 'Tabby', 'https://tabby.tabbyml.com/', 'images/tabby.webp', 'images/tabby-icon.webp', '{"what":"Tabby is an open-source, self-hosted AI coding assistant that aims to enhance and streamline the coding experience.","who":"Tabby is for developers and coders looking to improve their coding process with a reliable and customizable AI assistant.","best_features":["Utilizes Rust programming language for speed and safety.","Easy configuration through TOML config for personalized coding environment.","Open-source codebase for security transparency and control."],"pricing":[]}', 'Tabby is an open-source, self-hosted AI coding assistant that prioritizes speed, safety, and customization. It empowers users with an intuitive configuration process and offers a supportive community to enhance their coding experience.', NULL, '["AI Assistance","Coding Assistance"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('37c74747-96a9-4360-ae83-7b8dcd441dfb', '2024-04-12T15:29:31.626Z', 'TabHub', 'https://tabhub.io/', 'images/tabhub.webp', 'images/tabhub-icon.webp', '{"what":"TabHub is an all-in-one tab and link management platform that aims to enhance browsing efficiency and productivity by providing features such as smart grouping, time tracking, and team collaboration.","who":"TabHub is designed for individuals and teams who want to streamline their tab management, improve browsing productivity, and collaborate effectively in real-time.","best_features":["Smart Grouping: Utilizes AI to categorize tabs into well-structured groups for easy organization.","Time Tracker: Automatically tracks browsing activity and provides insightful analytics.","Team Collaboration: Allows users to share tabs with team members, collaborate in real-time, and manage access for team members."]}', 'TabHub is an all-in-one tab and link management platform that offers features such as smart grouping, time tracking, and team collaboration. It aims to streamline tab management, enhance browsing productivity, and improve collaboration efficiency. With a lightweight browser extension, users can organize tabs, track browsing activity, and collaborate with team members seamlessly. TabHub is currently in development and offers users the opportunity to join a waiting list for early access to the beta version.', NULL, '["Browser Extension","Team Collaboration","Tab Management"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('0616c5db-6406-4a1f-91e7-37979b8d2311', '2024-04-12T15:29:31.626Z', 'Tabirim', 'https://www.tabirim.co/', 'images/tabirim.webp', 'images/tabirim-icon.webp', '{"what":"Tabirim is an AI tool developed by Tabirim.co that interprets dreams using machine learning algorithms. Users can describe their dreams in detail and receive an interpretation based on the analysis of their input.","who":"Tabirim is designed for individuals curious about the meanings of their dreams and interested in exploring interpretation possibilities provided by AI technology.","best_features":["Uses machine learning algorithms for dream interpretation","Allows users to describe dreams in detail for accurate analysis","Provides interpretations as a source of inspiration and self-reflection"],"pricing":[]}', 'Akll Rya Tabircisi (Intelligent Dream Interpreter) is an AI tool developed by Tabirim.co that interprets dreams using machine learning algorithms. It offers users the opportunity to explore the meanings of their dreams with the help of AI technology, cautioning that interpretations should be taken as sources of inspiration and self-reflection.', NULL, '["Psychology","Self-Exploration","Dream Interpretation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('08380ca4-a566-467c-b695-6357be434ec0', '2024-04-12T15:29:31.627Z', 'Tableau', 'https://www.tableau.com/', 'images/tableau.webp', 'images/tableau-icon.webp', '{"what":"Tableau is a versatile business intelligence and analytics software designed to enable users to visualize, understand, and act on data. It offers advanced AI capabilities, a strong community aspect, and continuous development and innovation.","who":"Tableau is for organizations of various sizes and types looking to become more data-driven. It is suitable for individuals and teams in need of comprehensive analytics solutions.","best_features":["Ability to connect to almost any database","Intuitive, drag-and-drop interface for creating visualizations","Incorporation of advanced AI capabilities like Tableau Pulse and Einstein Copilot"],"pricing":[]}', 'Tableau, a product from Salesforce, is a versatile business intelligence and analytics software designed to enable users to visualize, understand, and act on data. It offers the ability to connect to almost any database, allowing users to create visualizations through an intuitive, drag-and-drop interface. As a comprehensive analytics platform, Tableau supports deployment on cloud, on-premises, or natively, satisfying a range of organizational deployment preferences.', NULL, '["Business Tools","AI Tools","Software"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1e8f6046-2b99-46c8-abdf-c1e16eb06995', '2024-04-12T15:29:31.627Z', 'TabMate', 'https://www.tabmate.co/', 'images/tabmate.webp', 'images/tabmate-icon.webp', '{"what":"TabMate is an AI-powered browser extension designed to efficiently manage excessive active browser tabs. It utilizes experimental AI technology to automatically categorize and organize tabs, offering users the flexibility to further customize their browsing environment.","who":"TabMate is ideal for individuals who frequently find themselves overwhelmed by numerous open tabs in their browser. It is especially beneficial for professionals, students, researchers, and anyone seeking to streamline their online workflow and boost productivity.","best_features":["Automatic tab categorization and organization using AI technology","Customizable tab groups and the ability to pin tabs and groups","Options for labeling tabs with text and color identification"]}', 'TabMate is an AI-powered browser extension that revolutionizes tab management by automatically categorizing and organizing tabs to declutter the browsing environment. With customizable features and robust AI technology, users can efficiently manage their tabs and enhance productivity. It is compatible with Google Chrome and most Chromium-based browsers on macOS and Windows platforms.', NULL, '["Browser Extension","AI Technology"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('7e462bba-23ae-465c-9c7a-97647c7de507', '2024-04-12T15:29:31.627Z', 'Tabula', 'https://www.tabula.io/', 'images/tabula.webp', 'images/tabula-icon.webp', '{"what":"Tabula is an AI-powered platform designed to support data transformation and visualization tasks for business and data teams. It serves as a comprehensive tool for making data-driven decisions without the need for extensive coding knowledge.","who":"Tabula is designed for marketing, business, and data teams looking to unify data from different areas, analyze customer journeys, and automate data pipelines. It is suitable for users who want to focus on business logic rather than coding.","best_features":["Automate data pipelines and manage data from over 150 sources","Easy-to-use visual data analytics tool for ad-hoc exploration and in-depth analytics","AI-powered platform for cleaning, formatting, and transforming data with no coding required"],"pricing":[]}', 'Tabula is an AI-powered platform that empowers marketing, business, and data teams to make data-driven decisions without extensive coding knowledge. It offers solutions for unifying data, analyzing customer journeys, and automating data pipelines. With a focus on business logic rather than coding, Tabula provides a user-friendly interface for cleaning, formatting, and transforming data. It serves as a single source of truth for data needs, connecting and aggregating data from over 150 sources.', NULL, '["AI Tools","Business Tools","Data Analysis"]');
