INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b91acd54-0cc1-4b3e-9027-20ed89d20974', '2024-04-12T15:29:31.663Z', 'tl;dr AI Summarizer', 'https://gettldr.app/', 'images/tl;dr-ai-summarizer.webp', 'images/tl;dr-ai-summarizer-icon.webp', '{"what":"tl;dr AI Summarizer is an AI-powered tool that offers instant article summarization, allowing users to quickly understand the key points of any article on the web.","who":"This product is designed for individuals who want to save time and stay informed by efficiently summarizing lengthy articles on the web.","best_features":["Instant article summarization for quick understanding","Efficiently generates summaries by cutting through unnecessary information","Helps users comprehend articles without reading them in full"],"pricing":[]}', 'tl;dr AI is an AI-powered tool that offers instant article summarization, allowing users to quickly understand the key points of any article on the web. By utilizing advanced algorithms, tl;dr AI efficiently generates summaries that cut through unnecessary information, assisting users in comprehending lengthy articles without having to read them in their entirety. The tool''s key features include the ability to summarize any article on the web, making it suitable for any type of online content. Whether users are looking to stay informed on the latest news or conduct research, tl;dr AI empowers them to stay up-to-date with important information without investing significant time in reading full articles.', NULL, '["AI Tools","Summarization"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('4f3b1285-2a46-41fd-9c36-3d6f5121bd71', '2024-04-12T15:29:31.663Z', 'tl;dv', 'https://tldv.io/', 'images/tl;dv.webp', 'images/tl;dv-icon.webp', '{"what":"tl;dv is a GPT-powered meeting software that records, transcribes, highlights, and shares online meetings to improve productivity for teams using Google Meet and Zoom.","who":"tl;dv is designed for teams and individuals who conduct online meetings and want to enhance productivity by automating meeting recordings, transcriptions, and summaries.","best_features":["Automatically record and transcribe Google Meet and Zoom calls in top quality","Receive highly accurate transcripts with speaker tags in 20 languages","Summarize meeting notes to boost productivity and make the most out of meetings"]}', 'tl;dv is a GPT-powered meeting software that automates the recording, transcription, and summarization of online meetings conducted on Google Meet and Zoom. It offers features such as transcription in multiple languages, speaker tagging, and summarized meeting notes to enhance productivity and collaboration within teams.', NULL, '["Productivity Tools","Transcription","AI Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('5eb9aa66-de4c-48d3-af76-28b8ce740bfe', '2024-04-12T15:29:31.663Z', 'TLDRAI', 'https://tldrai.com/', 'images/tldrai.webp', 'images/tldrai-icon.webp', '{"what":"TL;DR AI is a tool that enables users to summarize any text using artificial intelligence. It helps users to create concise and easy-to-digest summaries of lengthy content, allowing them to free themselves from information overload.","who":"TL;DR AI is for individuals who need to process large volumes of information but have limited time. It can be beneficial for students, researchers, professionals, or anyone who needs to quickly get an overview of long texts or documents.","best_features":["AI-powered text summarization","Summarization of text, files, and links/URLs","Efficient way to digest information and save time"]}', 'TL;DR AI is a tool that uses artificial intelligence to summarize any text, helping users create concise summaries of lengthy content. It offers different options for summarization, including text, files, and links/URLs. The tool condenses the text into key points, making it easier for users to grasp main ideas quickly without reading the entire text. TL;DR AI is particularly useful for individuals who need to process large volumes of information but have limited time. It provides a convenient and efficient way to summarize text using AI technology.', NULL, '["Summarization"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b8626881-b0d8-4dfc-a93a-51b520241db7', '2024-04-12T15:29:31.664Z', 'TldrGPT', 'https://www.tldrgpt.net/', 'images/tldrgpt.webp', 'images/tldrgpt-icon.webp', '{"what":"TL;DR GPTNet is an AI-powered tool designed to archive and generate summarized, readable versions of web pages, emphasizing the use of AI to condense content quickly and efficiently.","who":"This tool is for individuals who want to save time by quickly grasping the key points of lengthy articles, as well as for those who wish to preserve valuable web page content for future reference.","best_features":["AI-powered summarization of web pages for quick understanding","Ability to archive web pages for future reference","Convenient integration with Chrome and Brave browsers and multiple sharing options"]}', 'TL;DR GPTNet is an AI-driven web tool that enables users to generate condensed, understandable summaries of web pages and archive them for future reference. It offers straightforward integration with Chrome and Brave browsers, multiple sharing options, and the ability to quickly grasp key points of lengthy articles.', NULL, '["Content Creation","Web Tools"]');
