INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('de5095dc-37b7-48f2-ba66-2b143f2a6e7e', '2024-04-12T15:29:31.673Z', 'TraveLandz', 'https://travelandz.com/', 'images/travelandz.webp', 'images/travelandz-icon.webp', '{"what":"TraveLandz is an AI trip planner that offers personalized travel plans based on user preferences and real-time availability of hotels, attractions, and transportation.","who":"This product is designed for travelers looking to simplify the planning process and find the best prices for hotels and activities through partnerships with travel agencies.","best_features":["Personalized travel plans based on user preferences","Real-time availability of hotels, attractions, and transportation","User-friendly interface for easy navigation and exploration of unique destinations"],"pricing":[]}', 'Travelandz is an AI trip planner that simplifies the planning process for travelers by offering personalized travel plans based on user preferences and real-time availability of hotels, attractions, and transportation. The platform connects to real-time systems, allowing users to effortlessly plan and book every aspect of their vacation itinerary with confidence. Users can share their plans with the Travelandz community and receive valuable suggestions from fellow travelers.', NULL, '["AI Solutions","Travel Agency Technology"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3f65d370-9090-4794-a786-b1d71290a669', '2024-04-12T15:29:31.673Z', 'TravelFeed', 'https://travelfeed.io/ai', 'images/travelfeed.webp', 'images/travelfeed-icon.webp', '{"what":"TravelFeed''s AI Travel Blogging Assistant is a powerful tool designed for travel bloggers who are short on time. By utilizing artificial intelligence, this tool enables users to create high-quality travel blog content in just minutes.","who":"This product is for travel bloggers who want to save time and create professional-level travel blog content efficiently.","best_features":["AI-powered tool that helps generate unique and engaging titles for travel blog posts","Smart writing features to optimize content for SEO and engagement","Time-saving efficiency, allowing users to create exceptional travel blog posts in a fraction of the time it would take manually"],"pricing":[]}', 'TravelFeed''s AI Travel Blogging Assistant is a tool that helps travel bloggers create high-quality content in minutes. By following three simple steps, users can input their travel notes, revise the generated outline, and review/edit the blog post to reflect their personal voice and style. The tool offers smart writing features, unique titles generation, and time-saving efficiency, making it a valuable asset for travel bloggers.', NULL, '["Travel","Content Creation","Writing Assistance"]');
