INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('68f79669-d702-4b45-afe0-40b58de9417a', '2024-04-12T15:29:31.681Z', 'TubeHorizon', 'https://www.tubehorizon.ai/', 'images/tubehorizon.webp', 'images/tubehorizon-icon.webp', '{"what":"TubeHorizon is an advanced AI-driven analytics tool designed for YouTube creators seeking growth and success. It analyzes YouTube video comments to extract valuable insights into audience sentiment and popular topics.","who":"TubeHorizon is for YouTube creators who want to understand their audience better, improve engagement, and create content that resonates with viewer desires and sentiments.","best_features":["Ideation Engine for AI-driven content recommendations","Sentiment Analysis for understanding viewer sentiment","Engagement Amplifier for identifying engaging comments and viewer interaction"],"pricing":[]}', 'TubeHorizon is an AI-driven analytics tool for YouTube creators, offering insights into audience sentiment and popular topics from video comments. It helps creators plan content strategically, boost engagement, and grow their channels by understanding viewer feedback.', NULL, '["Content Creation","Social Media"]');
