INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e16f2d0d-531d-4802-9d6c-1728488484f7', '2024-04-12T15:29:31.682Z', 'TubeToText', 'https://tubetotext.com/', 'images/tubetotext.webp', 'images/tubetotext-icon.webp', '{"what":"TubeToText is an artificial intelligence tool designed to create instant, concise summaries from YouTube content. It applies advanced AI algorithms to provide accessible summaries with key takeaways, facilitating fast and effective learning.","who":"TubeToText is primarily targeted at users who wish to gain key insights from YouTube videos in a time-efficient manner. It is suitable for individuals looking to extract valuable information from informational videos, podcasts, tutorials, or any in-depth discussions.","best_features":["Instant and accurate YouTube video summaries","Facilitates fast and effective learning by providing key takeaways","Useful for high-density information or longer format videos"],"pricing":[]}', 'TubeToText is an AI tool that generates instant, concise summaries from YouTube content, allowing users to quickly extract key insights without watching the entire video. It is ideal for individuals seeking to learn efficiently from a wide range of video content, including tutorials, podcasts, and informational videos. TubeToText also serves as a valuable tool for content creators and educators looking to generate quick summaries of their content. Through its blog, TubeToText provides updates and insights about the tool, enhancing the user experience.', NULL, '["Content Creation","Education","Summarization"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('97048214-e4f6-42b0-8da1-0f3e5a73fe63', '2024-04-12T15:29:31.682Z', 'TuboGPT', 'https://tubogpt.com/', 'images/tubogpt.webp', 'images/tubogpt-icon.webp', '{"what":"TuboGPT is a chat client tool designed to enhance the functionality of ChatGPT by providing an improved version of the chat experience through AI language models.","who":"TuboGPT is for users who want to engage in dynamic and interactive conversations with an AI language model, seeking a more refined and engaging chat experience.","best_features":["Enhanced natural language understanding and generation through GPT3.5 and GPT4","Optimized conversation management for smoother chat flow","Simulating real-time interactions for a more natural and engaging conversational experience"],"pricing":[]}', 'TuboGPT is a chat client tool that serves as a mediator between the user and the chat model, enriching the chat experience by providing an improved version of ChatGPT. It leverages advanced language models to facilitate dynamic and engaging conversations, offering users a more refined and interactive chat experience.', NULL, '["Chat Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a47050f1-1545-4fc0-83aa-7d782fbf18e1', '2024-04-12T15:29:31.682Z', 'Tuesday', 'https://tuesday.so/', 'images/tuesday.webp', 'images/tuesday-icon.webp', '{"what":"Tuesday is an AI-powered spreadsheet tool that automates work processes by utilizing autonomous AI assistants. These assistants have the ability to browse the internet and provide answers to user queries.","who":"Tuesday is designed for individuals and teams looking to optimize their work processes and maximize productivity by streamlining various tasks and offering automation possibilities.","best_features":["AI assistants that can research each row of data, saving users time and effort","Capability to export data in CSV format for accessibility from anywhere","Combines spreadsheet functionality with web scraping and Google search integration"],"pricing":[]}', 'Tuesday is an AI-powered spreadsheet tool that utilizes autonomous AI assistants to automate work processes. Users can upload data, prompt the assistants to research each row of data, and export data in CSV format. The tool offers automation possibilities for tasks such as writing emails, researching leads, translating text, and more. Tuesday aims to simplify workflows and increase efficiency by harnessing the power of AI.', NULL, '["Spreadsheet Management","Automation","AI Assistance"]');
