INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('1ff6b029-2323-4a58-9b6d-51edc86a9ced', '2024-04-12T15:29:31.684Z', 'TuneFlow', 'https://tuneflow.com/', 'images/tuneflow.webp', 'images/tuneflow-icon.webp', '{"what":"TuneFlow is an intelligent music making platform powered by AI, designed to simplify and enhance music creation for users of all levels of expertise.","who":"TuneFlow is for musicians, music producers, content creators, and anyone interested in creating music with the help of AI technology.","best_features":["Voice Clone feature for selecting and cloning voices or generating new ones.","Smart Composer for kickstarting music ideas with pre-designed melodies and accompaniment tracks.","Ultra-Clean Source Separator for separating mixed audio tracks into individual stems."],"pricing":[]}', 'TuneFlow is an intelligent music making platform powered by AI, offering a range of powerful features such as Voice Clone, Smart Composer, and Ultra-Clean Source Separator. Users can easily create music, generate lyrics, and access industry-leading audio transcription. The platform also includes a plugin market, community collaboration, and advanced audio editing capabilities.', NULL, '["AI Music Production","Audio Editing","Collaborative Music Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('acb3bb34-bbe3-4a20-97f7-0ad7e0366bc0', '2024-04-12T15:29:31.684Z', 'TuneGPT', 'https://www.tunegpt.app/', 'images/tunegpt.webp', 'images/tunegpt-icon.webp', '{"what":"TuneGPT is an AI tool that allows users to train and deploy their own customized versions of ChatGPT, tailored specifically to their data. With TuneGPT, users can create AI models that have a wide range of applications, such as customer service chatbots and data analytics.","who":"TuneGPT is designed for individuals and businesses looking to create customized AI models for various applications such as customer service chatbots, data analytics, and AI automation.","best_features":["Seamless data upload for training process initiation","Accelerated model training for quicker deployment times","Intuitive model management tools for effortless deployment"]}', 'TuneGPT is an AI tool that empowers users to train and deploy their own customized ChatGPT models, offering versatility and ease of use across various industries. The tool provides a simple three-step process for training and deploying custom AI models, with seamless data upload, accelerated model training, and intuitive model management tools.', NULL, '["AI Solutions","Chat Solutions","Data Analysis"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f2455aeb-8cb6-43f6-84e4-3451cd189a31', '2024-04-12T15:29:31.684Z', 'Tunum Health', 'https://www.tunum.health/', 'images/tunum-health.webp', 'images/tunum-health-icon.webp', '{"what":"Tunum Health is a privacy-focused health and wellness companion that aggregates data from wearables to provide users with personalized daily insights and AI-powered health information.","who":"This product is designed for individuals who want to track, analyze, and optimize their health and wellness through intuitive data exploration and AI insights.","best_features":["Seamless aggregation of wearable data for comprehensive health tracking","AI-enabled chat function for personalized health insights","User-friendly journal feature for effortless wellness monitoring"],"pricing":[]}', 'Tunum Health is a privacy-focused health and wellness companion that allows users to seamlessly aggregate data from their wearables. The app provides daily health summaries, uncovers trends and relationships within personal health data, and offers AI-powered insights. Users can track mood, stress, recovery, and export their health data for informed care.', NULL, '["Wearable Technology","AI Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('fef05fe8-8f91-4523-803d-6a30d69c8401', '2024-04-12T15:29:31.684Z', 'Turbine', 'https://useturbine.com/', 'images/turbine.webp', 'images/turbine-icon.webp', '{"what":"Turbine is an automated data pipeline tool designed to support AI applications by synchronizing data from various databases for vector searches.","who":"Turbine is for businesses looking to power their AI bots with the latest language models without the hassle of infrastructure management.","best_features":["Seamless integration with popular databases like PostgreSQL, MongoDB, and MySQL.","Real-time synchronization of database changes for up-to-date semantic searches.","Support for storage of embeddings using leading vector databases and various embedding models."],"pricing":[]}', 'Turbine is an automated data pipeline tool that supports AI applications by synchronizing data from various databases for vector searches. It seamlessly integrates with popular databases, synchronizes changes in real-time, and supports storage of embeddings using leading vector databases. With extensive configurability and support for various embedding models, Turbine enables businesses to create AI applications that deliver accurate and context-rich results.', NULL, '["Data Pipeline","AI Applications","Vector Search Engine"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ed663805-09e7-4040-9be9-d66b0f3a7bc3', '2024-04-12T15:29:31.684Z', 'Turbo.Art', 'https://turbo.art/', 'images/turbo-art.webp', 'images/turbo-art-icon.svg', '{"what":"Turbo.Art is an artificial intelligence-powered image generation tool developed by Modal Labs. It utilizes Stability''s SDXL Turbo for image rendering, allowing users to create unique visuals in various styles.","who":"Turbo.Art is designed for artists, designers, content creators, and anyone interested in exploring the intersection of art and AI technology. It caters to individuals looking to enhance their creative process with AI assistance.","best_features":["AI-powered image generation tool with a wide range of styles and themes.","Enhance feature for refining generated images.","Open-source codebase on GitHub for customization and improvement."],"pricing":[]}', 'Turbo.Art is an AI-powered image generation tool that enables users to create unique visuals in various styles. It offers a canvas for drawing or providing prompts, with the AI generating new images based on the input. Users can refine the generated images with the ''Enhance'' feature and save them in high-quality formats. The tool is versatile, allowing for customization and improvement through its open-source codebase on GitHub.', NULL, '["Art Generation","Image Editing"]');
