INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('66221679-88b5-40e8-bf28-8da3fd7ed987', '2024-04-12T15:29:31.685Z', 'Tutor AI', 'https://www.speakenglishwithai.com/', 'images/tutor-ai.webp', 'images/tutor-ai-icon.webp', '{"what":"Tutor AI is an English-speaking application that allows individuals to practice their spoken English skills with the help of an AI chatbot. The AI chatbot offers real-time feedback on language improvement and provides various adjustable features for enhanced learning.","who":"Tutor AI is designed for individuals looking to improve their spoken English skills in a safe and non-judgmental environment. It is suitable for non-native English speakers, language learners, students, professionals, and anyone seeking to enhance their communication skills.","best_features":["Personalized learning experience tailored to individual needs and proficiency level","Interactive and immersive learning through engaging exercises and quizzes","Continuous progress tracking and real-time feedback for effective language improvement"],"pricing":[{"plan":"60 Minutes","price":3.99,"billing_cycle":"one-time purchase","original_price":0,"discount":"0%","features":["Real-time feedback","Adjustable conversation speed","Word-for-word translations"]},{"plan":"300 Minutes","price":12.99,"billing_cycle":"one-time purchase","original_price":0,"discount":"0%","features":["Personalized study plans","Helpful conversation prompts","Complete message translations"]},{"plan":"Unlimited","price":0,"billing_cycle":"subscription","original_price":0,"discount":"0%","features":["Access to all AI characters","Global learning community","Secure and private learning environment"]}]}', 'Tutor AI is an English-speaking application that helps users practice their spoken English skills with the assistance of AI chatbots. The app offers real-time feedback, personalized learning experiences, and interactive features to enhance language proficiency. Users can select from various AI characters and engage in natural conversations to improve vocabulary and communication style.', NULL, '["Language Learning","AI Tools","Communication Skills"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a31fd889-54c2-4fa2-96cc-a5b93b498463', '2024-04-12T15:29:31.685Z', 'TutorAi App', 'https://tutorai.app/en/home/', 'images/tutorai-app.webp', 'images/tutorai-app-icon.webp', '{"what":"TutorAi is an AI-powered learning tool designed to enhance and optimize your learning experience by combining Ultralearning techniques and a powerful generative AI engine.","who":"TutorAi is for advanced English students who are looking for a more professional and time-saving learning experience.","best_features":["Engaging and personalized content on a variety of topics such as Business English, Legal English, Sports, Sustainability, and more.","Study plan with gap analysis for personalized learning, whether studying alone or with a teacher.","Integration of the largest English skills map with over 400 skills to optimize learning and performance."],"pricing":[{"plan":"Free access","price":0,"billing_cycle":"N/A","original_price":0,"discount":"N/A","features":["Learn alone","Practice daily","Ask questions"]},{"plan":"Monthly subscription","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"N/A","features":["Progressive Quizzes","Grammar Diagnosis","Personalized Study Plan"]}]}', 'TutorAi is an AI-powered learning tool that combines Ultralearning techniques and a generative AI engine to provide advanced English students with an engaging and personalized learning experience. With a focus on optimizing learning and performance, TutorAi offers a study plan with gap analysis, integration of a vast English skills map, and a range of topics to choose from. Whether studying alone or with a teacher, TutorAi aims to revolutionize English learning and help individuals achieve professional and personal success in a globalized world.', NULL, '["Language Learning","AI Technology","Personalized Learning"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f880ad55-b29c-4482-83fe-f2927a75e57c', '2024-04-12T15:29:31.686Z', 'Tutorly', 'https://tutorly.ai/', 'images/tutorly.webp', 'images/tutorly-icon.webp', '{"what":"Tutorly.ai is an AI-powered homework help tool that offers a chat-based interface to students. Users can ask questions, get help with essays, and paraphrasing. It also provides additional services for users who upgrade their account.","who":"Tutorly.ai is designed for students who need assistance with homework, essays, and paraphrasing. It is also suitable for those looking for additional services to enhance their learning experience.","best_features":["AI-powered assistance for homework help","Chat-based interface for easy communication","Additional services available for upgraded accounts"],"pricing":[]}', 'Tutorly.ai is an AI-powered homework help tool that provides students with a chat-based interface to ask questions, get help with essays, and paraphrasing. Users can upgrade their account to access additional services and enhance their learning experience.', NULL, '["AI Assistance","Chat Solutions","Tutoring"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('795096d4-29af-4a30-91da-634d518cee92', '2024-04-12T15:29:31.686Z', 'TutorMind', 'https://www.tutor-mind.com/', 'images/tutormind.webp', 'images/tutormind-icon.webp', '{"what":"Tutor Mind is an AI-powered tool designed to simplify teaching for educators by offering features such as lesson planning, essay checking, question generation, and plagiarism detection. It aims to save time and streamline the workload for teachers, ultimately enhancing the quality of education provided.","who":"Tutor Mind is ideal for teachers, educators, and academic professionals looking to optimize their teaching process and improve efficiency. It is also beneficial for educational institutions seeking innovative tools to enhance the teaching and learning experience.","best_features":["Advanced AI-powered lesson planning for efficient curriculum organization","Essay checking and plagiarism detection for ensuring academic integrity","Question generation tool for creating engaging and interactive learning materials"],"pricing":""}', 'Tutor Mind is an AI-powered tool that simplifies teaching by providing features such as lesson planning, essay checking, question generation, and plagiarism detection for teachers. It is designed to save time and make the workload easier for teachers, providing quality education. It offers four powerful tools to save hundreds of hours by allowing teachers to plan lessons, mark essays, and detect plagiarism quickly and easily. The tool also includes an advanced AI detector to check past student texts for authenticity instantly. Tutor Mind also provides an optimized large-screen mode for an excellent user experience.', NULL, '["AI Assistance","Teacher Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9864ccf6-9f86-411d-8b1a-26768c986626', '2024-04-12T15:29:31.686Z', 'Tutory', 'https://www.tutory.io/', 'images/tutory.webp', 'images/tutory-icon.webp', '{"what":"Tutory is an AI-powered tutor and teaching assistant designed to enhance learning through personalized, on-demand help. It is a learning companion accessible to students of all ages and skill levels.","who":"Tutory is for learners seeking personalized assistance, teachers looking for classroom support, and parents wanting to be involved in their child''s education. It is suitable for individuals, schools, and educational institutions.","best_features":["Socratically focused guidance towards understanding concepts","Adaptive memory for personalized learning experiences","Intuitive and fun learning techniques using everyday language"]}', 'Tutory is an AI-powered tutor and teaching assistant that offers personalized, on-demand help to enhance the learning experience. It focuses on guiding learners towards understanding concepts, adapting to individual learning needs, and making learning fun and intuitive. Tutory is accessible to students, teachers, and parents, providing support in various educational settings.', NULL, '["AI Tutoring","Teaching Assistant"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('936fb39b-e828-4d41-8b21-82d8ba594755', '2024-04-12T15:29:31.686Z', 'Tutur', 'https://tutur.io/', 'images/tutur.webp', 'images/tutur-icon.svg', '{"what":"Tutur is an advanced online platform that utilizes artificial intelligence to aid language learning. It offers a versatile set of tools designed to boost language proficiency.","who":"Tutur is for individuals looking to enhance their language skills in a convenient and interactive way. It caters to learners of all levels who seek personalized feedback and immersive practice.","best_features":["Detailed speech assessments for pinpointing areas of improvement","One-on-one conversations with an AI tutor for immersive learning","User-friendly dashboard for monitoring progress and customization"],"pricing":[{"plan":"Basic","price":5,"billing_cycle":"month","original_price":10,"discount":"50%","features":["Access to all scenes","Replay conversations","5 daily conversations","20 messages per conversation"]},{"plan":"Pro","price":10,"billing_cycle":"month","original_price":20,"discount":"50%","features":["Access to all scenes","Replay conversations","Unlimited conversations","Unlimited messages","Detailed speech insight"]},{"plan":"Enterprise","price":null,"billing_cycle":"","original_price":null,"discount":"","features":[]}]}', 'Tutur is an AI-powered language learning platform that offers detailed speech assessments, one-on-one conversations with AI tutors, and a user-friendly dashboard for monitoring progress. With support for multiple languages and various scenarios, Tutur provides an immersive and authentic learning experience. The platform also offers flexible pricing plans tailored to different needs.', NULL, '["Language Learning","AI Technology"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('570a4afd-49bd-4318-abff-0cf8134c6780', '2024-04-12T15:29:31.686Z', 'Tweeets', 'https://tweeets.app/', 'images/tweeets.webp', 'images/tweeets-icon.webp', '{"what":"Tweeets is an AI-powered tool designed to simplify the tweeting process for regular Twitter users by enabling them to generate tweets based on their own tweets or specific parameters.","who":"Tweeets is for individuals who want to easily create customized tweets tailored to their specific needs or preferences, without the hassle of coming up with content from scratch.","best_features":["Customizable parameters for context, sentiment, writing style, tone, and audience target","Utilizes OpenAI''s GPT-3 AI model for tweet generation","Option to save preferred parameters and share tweet models with others"],"pricing":[]}', 'Tweeets is an AI-powered tool that simplifies the tweeting process for regular Twitter users by allowing them to generate customized tweets based on their own tweets or specific parameters. Users can easily analyze and create tweets with customizable context, sentiment, writing style, tone, and audience target. The tool utilizes OpenAI''s GPT-3 AI model and requires users to log in with their Twitter account to save preferences and generate tweets. With Tweeets, users can effortlessly construct tweets tailored to their needs and enhance their overall tweeting experience.', NULL, '["AI Tools","Content Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c0dcba93-eb71-430e-8cd6-607b927c54e6', '2024-04-12T15:29:31.686Z', 'Tweet Cycle', 'https://tweetcycle.com/', 'images/tweet-cycle.webp', 'images/tweet-cycle-icon.webp', '{"what":"Tweet Cycle is an AI-powered tool for generating and scheduling tweets, tailored to the user''s unique voice using AI technology. It also offers contact and lead management features for Twitter.","who":"Tweet Cycle is designed for social media managers, content creators, businesses, and individuals looking to enhance their Twitter presence and engagement. It is ideal for those who want to streamline their tweet creation and scheduling process.","best_features":["AI-powered tweet generation for personalized content creation.","Advanced scheduling capabilities for planning tweets and threads in advance.","Contact and lead management features for Twitter, including lead generation and customer service support."],"pricing":{"plan":"Premium","price":9.99,"billing_cycle":"monthly","original_price":14.99,"discount":"33% off","features":["AI-powered tweet generation","Advanced scheduling capabilities","Contact and lead management features"]}}', 'Tweet Cycle is an AI-powered tool for generating and scheduling tweets, with advanced features for contact and lead management on Twitter. It offers a 7-day free trial with no credit card required and a premium plan with a simple pricing structure.', NULL, '["AI Tools","Marketing"]');
