INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('cd05f9e3-cafc-43f4-9f5d-445e4c28fca4', '2024-04-12T15:29:31.687Z', 'TweetAI', 'https://tweetai.com/', 'images/tweetai.webp', 'images/tweetai-icon.webp', '{"what":"TweetAI is an AI tool that generates tweets based on user inputs and selected tweet types like jokes, questions, and educational content. It aims to help users overcome writer''s block and grow their audience faster.","who":"TweetAI is designed for individual developers, social media marketers, marketing agencies, digital creators, ghostwriters, and anyone looking to boost engagement and grow their followers on Twitter.","best_features":["One-click replies that sound like you","Fine-tuned on millions of tweets","Boost engagement & grow your followers fast"],"pricing":[{"plan":"Starter","price":9.99,"billing_cycle":"monthly","original_price":17,"discount":"41% off","features":["Single seat only","1000 replies/tweets per month","Includes 500 FREE replies/tweets"]},{"plan":"Business","price":49,"billing_cycle":"monthly","original_price":null,"discount":"","features":["Unlimited seats","4000 replies/tweets per month","Includes 1000 FREE replies/tweets"]},{"plan":"Enterprise","price":199,"billing_cycle":"monthly","original_price":null,"discount":"","features":["Unlimited seats","16000 replies/tweets per month","Includes 2000 FREE replies/tweets"]}]}', 'TweetAI is an AI tool that helps users generate creative tweets and replies quickly. With features like one-click replies, fine-tuning on millions of tweets, and the ability to boost engagement and grow followers fast, TweetAI is a valuable tool for individuals and businesses looking to enhance their Twitter presence.', NULL, '["AI Tools","Marketing","Content Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('165ea0f3-991a-484c-8b5b-a90834796ddb', '2024-04-12T15:29:31.687Z', 'TweetDetective', 'https://www.tweetdetective.com/', 'images/tweetdetective.webp', 'images/tweetdetective-icon.webp', '{"what":"TweetDetective is an AI-powered tool that assists users in identifying AI-generated content on Twitter by analyzing tweets and revealing the likelihood of AI-generated content.","who":"This product is for Twitter users who want to distinguish between authentic and AI-generated content on their feed.","best_features":["Advanced AI Analysis for accurate detection of AI-generated content","Rapid Scans for quick identification of AI-generated text","Secure data handling with no third-party sharing of personal information"],"pricing":[{"plan":"Pro Detective","price":9.99,"billing_cycle":"Monthly subscription","original_price":19.99,"discount":"50% off","features":["Unlimited use of Chrome Extension","Detection of popular AI models like ChatGPT & Bard","97% accuracy with constant updates and 24/7 support"]}]}', 'TweetDetective is an AI tool that helps users detect AI-generated content on Twitter with high accuracy and security. Users can install a Chrome extension, input an API key, and see real-time probability percentages of AI-generated tweets. The tool is designed for authenticity and offers a fast and user-friendly experience.', NULL, '["Social Media","Data Analysis"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('34ae8929-2c20-4f32-a204-f1fc563e58d7', '2024-04-12T15:29:31.687Z', 'Tweetfox', 'https://tweetfox.io/', 'images/tweetfox.webp', 'images/tweetfox-icon.webp', '{"what":"Tweetfox is a smart Twitter automation platform that uses AI to create high-quality, engaging content and grow your audience quickly.","who":"TweetFOX is for beginners looking to build their personal brand, Twelebs looking for consistency, and Managers scaling multiple accounts.","best_features":["AI-assisted tweet creator can write unique tweets in seconds","Thread creator can write full-length threads in just three clicks","Deep analytics and growth lab to help understand what works best and find the right audience to engage with"],"pricing":[{"plan":"Free forever plan","price":0,"billing_cycle":"forever","original_price":0,"discount":"N/A","features":["Unlimited tweets and threads creation","AI-assisted tweet and thread creation","Deep analytics and growth lab"]}]}', 'Tweetfox is a smart Twitter automation platform that uses AI to create high-quality, engaging content and grow your audience quickly. Their AI-assisted tweet creator can write unique tweets in seconds, while the thread creator can write full-length threads in just three clicks. The platform also offers deep analytics and a growth lab to help you understand what works best and find the right audience to engage with. The basic plan is free forever, and there are no limits on how many tweets or threads you can create.', NULL, '["AI Assistance","Content Creation","Analytics"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2ed4b298-30bf-4201-8199-b87f7bf423fb', '2024-04-12T15:29:31.687Z', 'Tweetify It', 'https://tweetify.it/', 'images/tweetify-it.webp', 'images/tweetify-it-icon.webp', '{"what":"Tweetify It is an AI tool that transforms long-form content into short social media posts for various platforms like Twitter, Facebook, Instagram, and LinkedIn. It personalizes posts with tone, language, @mentions, #hashtags, and emojis.","who":"Tweetify It is designed for content creators, agencies, social media managers, and anyone looking to efficiently repurpose long-form content into engaging social media posts.","best_features":["AI-powered content transformation for multiple social media platforms","Personalization with tone, language, @mentions, #hashtags, and emojis","Support for different languages and translations"],"pricing":[{"plan":"Free Trial","price":0,"billing_cycle":"One-time","original_price":0,"discount":"100% off","features":["100 lifetime credits","Personalized social media posts","Multi-platform support"]},{"plan":"Pay-as-you-go","price":null,"billing_cycle":null,"original_price":null,"discount":null,"features":[]}]}', 'Tweetify It is an AI tool that streamlines the process of converting lengthy content into concise and engaging social media posts across various platforms. It offers personalized posts with tone, language, @mentions, #hashtags, and emojis, making it ideal for content creators and agencies seeking to maximize their social media presence. With support for different languages and translations, Tweetify It ensures that content remains relevant and accessible to a global audience.', NULL, '["Social Media","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a6743284-78bc-4cc4-96ad-9e962fa6e8bd', '2024-04-12T15:29:31.687Z', 'Tweetspear', 'https://www.tweetspear.com/', 'images/tweetspear.webp', 'images/tweetspear-icon.webp', '{"what":"Tweetspear is an AI tool designed to boost Twitter engagement through suggested replies, thread generation, text paraphrasing, and tone customization. It aims to enhance response quality and impact while saving time and effort.","who":"Tweetspear is for individuals, businesses, influencers, and creators looking to improve their Twitter engagement, content creation, and audience growth.","best_features":["AI-powered suggested replies for quick and accurate responses","Thread generator for creating engaging tweet threads","Text paraphrasing and tone customization for personalized interactions"],"pricing":[{"plan":"Yearly Subscription","price":9,"billing_cycle":"per month","original_price":108,"discount":"20% off with code BLACKFRIDAY2023","features":["Engagement + Content = Grow","Analytics for posting and engagement","Behavior analytics for followers and activity"]},{"plan":"Lifetime Access","price":219,"billing_cycle":"one-time payment","original_price":219,"discount":"","features":["Unlimited custom feeds","Unlimited post scheduling","Tag tweets and remove ads"]}]}', 'Tweet Spear is an AI tool developed by Fernando Palacio to enhance Twitter engagement by providing suggested replies, thread generation, text paraphrasing, and tone customization. It offers a free trial, supports multiple languages, and aims to improve response quality and impact while saving time and effort.', NULL, '["AI Tools","Engagement","Content Creation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('10f99f36-f414-42d7-acb0-65024e36b823', '2024-04-12T15:29:31.687Z', 'Twelve Labs', 'https://twelvelabs.io/', 'images/twelve-labs.webp', 'images/twelve-labs-icon.svg', '{"what":"Twelve Labs is a video search AI tool that can extract key features from videos, such as actions, objects, speech, people, and text. It can transform this information into vector representation for fast and scalable semantic search.","who":"Twelve Labs is designed for businesses and individuals who work with large amounts of video data and need a powerful tool to extract valuable insights and information from their video libraries.","best_features":["State-of-the-art accuracy in video understanding","Powerful multimodal search capabilities","Customizable to domain-specific needs"],"pricing":[{"plan":"Standard","price":99,"billing_cycle":"monthly","original_price":129,"discount":"23% off","features":["Unlimited video indexing","API integration","Customizable search parameters"]},{"plan":"Enterprise","price":299,"billing_cycle":"monthly","original_price":399,"discount":"25% off","features":["Advanced video analytics","Dedicated support","Custom model training"]}]}', 'Twelve Labs is a cutting-edge video search AI tool that enables users to extract key features from videos and perform semantic searches. It offers state-of-the-art accuracy, powerful multimodal search capabilities, and customization options for domain-specific needs. With an easy-to-use API, users can integrate Twelve Labs into any platform to enhance video understanding and analysis.', NULL, '["Video Analysis","Semantic Search","Multimodal AI"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('3a2d8f4c-eeeb-4dcd-9b71-7fb2f81e5a58', '2024-04-12T15:29:31.687Z', 'Twig AI', 'https://www.twig.so/', 'images/twig-ai.webp', 'images/twig-ai-icon.webp', '{"what":"Twig AI is an AI tool designed by TeamSync Software Inc. to augment support agents by utilizing semantic analysis of documents, knowledge base, and past support tickets to answer complex and custom questions, ultimately reducing costs.","who":"Twig AI is designed for customer experience teams, support engineers, CX leaders, and professionals who aim to increase product quality, reduce support variance, and improve response time.","best_features":["AI Brain built for CX teams, providing factual answers that are always cited with trustworthy sources.","Control how AI responds to questions, allowing CX teams to review AI answers and add tribal knowledge.","Personalized AI in your own style, enabling users to set up preferences such as answering style and tone."],"pricing":[]}', 'Twig AI is an AI tool by TeamSync Software Inc. that reads, analyzes, and writes responses to customer queries from help docs, private datasources, and past support tickets. It offers unparalleled features and benefits such as 2X faster first responses, AI personas mapping to multiple CX roles, and insights to help oversee and refine AI. Twig AI has been positively received by CX leaders for its ability to save time, improve response quality, and enhance customer experience.', NULL, '["Customer Support","Data Analysis","AI Solutions"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('419c34e4-b3e7-414d-91f5-a4f6afc03718', '2024-04-12T15:29:31.688Z', 'Twilix', 'https://www.twilix.io/', 'images/twilix.webp', 'images/twilix-icon.webp', '{"what":"Twilix is an artificial intelligence company that offers easy-to-use APIs for developers to integrate AI into their products. The company provides a hosted vector database, infrastructure for fine-tuning models, and various AI features for semantic search, text summarization, and more.","who":"Twilix is designed for developers, small businesses, agencies, and startups looking to incorporate AI features into their products without the hassle of building AI models from scratch.","best_features":["Hosted vector database for secure and fully-managed features","Infrastructure for fine-tuning models and utilizing own models through APIs","APIs for semantic search, text summarization, and generative question answering"]}', 'Twilix offers a comprehensive AI solution with easy-to-use APIs for developers to integrate AI features into their products. The company provides a hosted vector database, infrastructure for fine-tuning models, and various AI features for semantic search, text summarization, and more.', NULL, '["API Integration","AI Features","Model Fine-tuning"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('416774a2-cf39-4471-a513-78e9ec3bf172', '2024-04-12T15:29:31.688Z', 'Twin Pics', 'https://twinpics.ai/', 'images/twin-pics.webp', 'images/twin-pics-icon.webp', '{"what":"Twin Pics is an AI-based tool that allows users to generate images that match a given reference image, aiming for accuracy and creativity.","who":"Twin Pics is designed for individuals interested in exploring the capabilities of AI in image creation, as well as those looking for a fun and engaging platform to test their creativity.","best_features":["AI-based image generation for accurate replication of reference images.","Daily leaderboard to showcase top user-generated images and descriptions.","Interactive and engaging platform to explore AI in visual creativity."],"pricing":[]}', 'Twin Pics is an AI-based tool that enables users to generate images matching a reference image through artificial intelligence algorithms. Users describe an image, and the system creates a corresponding graphic, scoring and ranking users based on accuracy. Created by Chris Sev, Twin Pics offers an interactive platform to explore AI in image creation, emphasizing the role of AI in visually creative fields.', NULL, '["Image Generation","AI Tools","Technology"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('002c1d27-19f9-4046-a37f-f23bbbbcf906', '2024-04-12T15:29:31.688Z', 'Twine', 'https://www.usetwine.com/', 'images/twine.webp', 'images/twine-icon.webp', '{"what":"Twine is an AI receptionist designed to help businesses capture and respond to leads, schedule appointments, process payments, and follow up with customers automatically.","who":"Twine is for businesses looking to improve customer service, increase lead conversion, and streamline appointment scheduling and payment processing.","best_features":["24/7 Service: Twine answers business inquiries round the clock, providing peace of mind to business owners.","Bespoke Customization: Twine can be tailored to work seamlessly with any specific business setup.","SMS Payments: Get invoices paid quicker with Twine''s SMS payment feature."]}', 'Twine is an AI receptionist service that helps businesses manage customer inquiries, appointments, payments, and reviews seamlessly. With 24/7 availability, bespoke customization options, and SMS payment capabilities, Twine offers a comprehensive solution for businesses looking to enhance their customer service and operational efficiency.', NULL, '["Customer Service","Appointment Scheduling","Payment Processing"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('9a5e7c97-c28d-40d0-a2fe-9ebde0e5d779', '2024-04-12T15:29:31.688Z', 'Twinit', 'https://twinit.ai/', 'images/twinit.webp', 'images/twinit-icon.webp', '{"what":"Twinit is an AI solution designed to enhance customer experience through customizable AI chat features and vibrant digital identities.","who":"Twinit is for businesses looking to improve customer engagement and communication through advanced AI technology.","best_features":["Customizable AI chat features for personalized interactions with users.","Vibrant digital identities for AI characters to enhance visual communication.","3D human reconstruction for transforming users'' look into dynamic digital identity."],"pricing":[]}', 'Twinit is an AI solution that facilitates communication through customizable AI chat features and vibrant digital identities. It offers visual and voice options for AI characters to interact with users in a unique way. Twinit provides generic and specific character profiles, along with real-time AI chat motion and 3D human reconstruction features. Recognized for its cutting-edge technology, Twinit promises a unique human-AI interaction experience to enrich customer engagements.', NULL, '["Customer Engagement","Digital Identity","Communication"]');
