INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e7a30348-bad7-4ba9-9dfa-904fde8864c8', '2024-04-12T15:29:31.688Z', 'Twip AI', 'https://www.twip.ai/', 'images/twip-ai.webp', 'images/twip-ai-icon.svg', '{"what":"Twip AI is an artificial intelligence tool designed to assist with content creation for YouTube. It combines advanced scriptwriting, idea generation, and voice cloning functionalities for a comprehensive content creation suite.","who":"Twip AI is for content creators on YouTube looking to streamline their content creation process, generate personalized scripts, optimize video SEO, and enhance their overall workflow efficiency.","best_features":["Automatically generate personalized scripts tailored to the user''s voice and style","Recommend high-performing content ideas based on the user''s YouTube channel","Provide video review and optimization with SEO-friendly suggestions"]}', 'Twip AI is an AI tool for YouTube content creation, offering scriptwriting, idea generation, voice cloning, and video optimization features. It helps users overcome writer''s block, personalize scripts, and improve SEO for videos.', NULL, '["AI Tools","YouTube","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('50b62542-3c14-4a4a-b175-9626a1b13836', '2024-04-12T15:29:31.689Z', 'Twitter Bio Generator', 'https://www.twitterbio.io/', 'images/twitter-bio-generator.webp', 'images/twitter-bio-generator-icon', '{"what":"Twitter Bio Generator is an AI tool designed to create unique and creative Twitter bios using Mixtral and GPT-3.5 from OpenAI.","who":"This product is for individuals who want a smart and easy way to generate engaging Twitter bios without having to write them themselves.","best_features":["Adaptability to handle a wide range of personal details and tone preferences.","Uses GPT-3.5 to effectively understand user inputs and generate personalized outputs.","Open-source project, allowing developers to examine and contribute to the source code."]}', 'Twitter Bio Generator is an AI tool that uses Mixtral and GPT-3.5 to generate unique and creative Twitter bios based on user inputs. It offers adaptability, personalized outputs, and open-source capabilities, making it a convenient solution for creating engaging Twitter bios.', NULL, '["Content Creation","Social Media"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e8bd3e44-4d7c-4091-becf-23c4f4231168', '2024-04-12T15:29:31.689Z', 'TwitterBookmarks', 'https://twitterbookmarks.com/', 'images/twitterbookmarks.webp', 'images/twitterbookmarks-icon.webp', '{"what":"TwitterBookmarks is an AI-assisted tool designed to import, categorize, and search Twitter bookmarks, transforming them into a well-structured and easily accessible format.","who":"This product is for Twitter users who want to efficiently organize and access their saved bookmarks on the platform.","best_features":["AI-assisted categorization of bookmarks for easy navigation","Ability to create custom categories for personalized organization","Bulk export options in CSV and JSON formats for data management"],"pricing":""}', 'TwitterBookmarks is a Bookmarks Manager powered by GPT-4 AI technology, aimed at simplifying the Twitter bookmarking experience. It automatically imports and categorizes bookmarks, allowing users to easily search and access saved tweets. The tool offers customization options for categorization and bulk export features for efficient data management.', NULL, '["AI Assistance","Data Management"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('45086997-dd64-46e6-94db-18b1b0b2f98f', '2024-04-12T15:29:31.689Z', 'Twogether', 'https://twogether.ai/create', 'images/twogether.webp', 'images/twogether-icon.webp', '{"what":"Twogether AI is an innovative artificial intelligence tool that generates multi-person portraits by blending two images together using advanced AI technology.","who":"This product is for individuals looking to create unique digital art pieces by combining their image with someone else''s, offering a creative and fun way to generate personalized artwork.","best_features":["Advanced AI technology for seamless blending of two images","Ability to create truly unique and personalized art pieces","Web-based tool with easy access and user-friendly interface"],"pricing":[]}', 'Twogether AI is a tool that allows users to merge two photographs into one, creating composite images that combine the features of both subjects. The proprietary algorithm generates new and unique combinations, offering a creative way to generate personalized digital art. Users can try the tool for free by signing in through their Google accounts and agreeing to the service terms and privacy policy.', NULL, '["AI Tools","Image Editing"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('6d7e244a-3102-4f11-b8a2-3172ecf39741', '2024-04-12T15:29:31.689Z', 'Twon', 'https://www.twon.app/', 'images/twon.webp', 'images/twon-icon.webp', '{"what":"TWON is an AI tool designed to help users leverage Twitter effectively by exploring different tweet possibilities and formats. It offers features like Nodes for visualizing tweet variations, format options like Expand and Improve, and Personalized Favorites for curating favorite variations.","who":"TWON is for individuals and businesses looking to improve their Twitter engagement and maximize the impact of their tweets. It is suited for users who want to unleash their creativity and explore endless tweet possibilities.","best_features":["Nodes feature for visualizing and exploring endless tweet variations","Format options like Expand, Improve, and Grammar for creative expression","Personalized Favorites feature for curating and saving favorite tweet variations"],"pricing":""}', 'TWON is an AI tool that empowers users to make the most of Twitter by providing a platform to explore tweet possibilities and formats. It offers features like Nodes, Format options, and Personalized Favorites to help users unleash their tweeting potential. TWON is available through an early access program by Serudda and Zyruks.', NULL, '["AI Tools","Marketing"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ba0d40c0-ea17-489d-8235-659fa9f63e85', '2024-04-12T15:29:31.690Z', 'TwoShot', 'https://twoshot.ai/', 'images/twoshot.webp', 'images/twoshot-icon.svg', '{"what":"TwoShot is an AI tool that revolutionizes music sampling by offering a vast collection of samples and AI Sample Generation capabilities.","who":"TwoShot is designed for musicians, producers, and creators looking to explore a diverse library of music samples and enhance their creativity in music production.","best_features":["Extensive collection of over 200,000 samples from indie creators to music labels","AI Sample Generation for creating custom samples based on user instructions","Streamlined user experience with a plugin for seamless integration into existing workflows"],"pricing":[]}', 'TwoShot is an AI tool that redefines music sampling with its extensive sample library and AI Sample Generation feature. Users can access over 200,000 samples ranging from old-school chops to hyper-pop melodies. The AI capability allows users to generate custom samples by providing simple instructions, unlocking endless creative possibilities. TwoShot offers a user-friendly experience with a plugin for easy integration, empowering musicians to find inspiration and produce high-quality music effortlessly.', NULL, '["AI Tools","Music Production"]');
