INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('bb6db740-6cca-4adc-af2f-e0873d6dae23', '2024-04-12T15:29:31.700Z', 'Unspam', 'https://unspam.io/', 'images/unspam.webp', 'images/unspam-icon.svg', '{"what":"Unspam is an AI-powered tool designed to streamline your inbox by filtering out unsolicited emails.","who":"Unspam is for individuals and businesses who receive a high volume of emails and want to declutter their inbox from spam messages.","best_features":["Advanced artificial intelligence for effective spam identification and handling","Automatic labeling or archiving of potential spam emails","Privacy-focused with strict privacy provisions, ensuring no email storage or logging"],"pricing":[]}', 'Unspam is an AI-powered tool compatible with Gmail and Google Workspace that filters out unsolicited emails, providing a clutter-free inbox experience. The tool scans new emails for potential spam, labels or archives them automatically, and prioritizes user convenience and security. Unspam does not store or log emails unless manually forwarded for training purposes. Originally created for entrepreneurs, Unspam empowers users to refocus on important tasks by minimizing email disruptions.', NULL, '["AI Tools","Productivity Tools"]');
