INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('59f1ee90-2d6d-463f-a29b-4089ae86fbd0', '2024-04-12T15:29:31.719Z', 'Videograph', 'https://www.videograph.ai/landscape-to-vertical/', 'images/videograph.webp', 'images/videograph-icon.webp', '{"what":"Videograph is an AI-Based Portrait PRO video cropping tool that uses artificial intelligence technology to convert landscape videos to portrait ratio, enhancing the viewing experience on mobile and social media platforms.","who":"This product is designed for content creators, social media influencers, and anyone looking to transform their landscape-oriented videos into engaging portrait format.","best_features":["Advanced AI algorithm for accurate subject identification and cropping","Time-saving automated cropping process","Preservation of important details and visually appealing portrait layout"],"pricing":[]}', 'Videograph is an AI-Based Portrait PRO video cropping tool that seamlessly converts landscape videos to portrait ratio using advanced AI algorithms. It intelligently identifies and crops subjects in each frame, ensuring no essential content is lost during the conversion. This tool is ideal for enhancing the visual appeal and optimizing the viewing experience of videos for various platforms.', NULL, '["AI Tools","Content Creation"]');
