INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('befe3b67-18e2-441d-8f3a-70dfaf3fd90e', '2024-04-12T15:29:31.719Z', 'VideoMuse', 'https://www.videomuse.app/', 'images/videomuse.webp', 'images/videomuse-icon.webp', '{"what":"VideoMuse is an AI-driven brainstorming tool designed to help YouTube content creators generate new video ideas effortlessly.","who":"VideoMuse is for YouTube content creators who are looking to save time and increase their video content''s creative potential by leveraging AI technology for brainstorming.","best_features":["AI-driven inspiration for generating over 443 pre-existing video ideas","User-friendly interface for easy navigation","No credit card required to get started"],"pricing":[{"plan":"Free Plan","price":0,"billing_cycle":"One-time","original_price":0,"discount":"N/A","features":["Generate over 443 pre-existing video ideas","User-friendly interface","No credit card required"]}]}', 'VideoMuse is an artificial intelligence (AI) tool that assists YouTube content creators in generating new video ideas. By leveraging AI technology, the platform provides over 443 pre-existing ideas that can be iterated on, eliminating the need for creators to spend hours brainstorming. With a simple process of connecting their YouTube channel and clicking a button, creators can access AI-driven inspiration to enhance their video content''s creative potential. VideoMuse focuses on creative brainstorming and aims to jump-start creativity for content creators, offering a user-friendly interface for both experienced and inexperienced creators.', NULL, '["AI Solutions","Video Editing"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('66ad318c-5fdb-412b-a6b9-18e0fe1cc8e2', '2024-04-12T15:29:31.719Z', 'VideoSage', 'https://www.videosage.ai/', 'images/videosage.webp', 'images/videosage-icon.webp', '{"what":"VideoSage is an AI-powered tool designed to enhance the experience of watching long videos by providing insightful summaries, insights, and timestamps. It allows users to ask questions about the video content and engage in conversations with the AI while watching, fostering a collaborative environment.","who":"VideoSage is for individuals who watch long videos and seek a more interactive and customized viewing experience. It is also beneficial for those who want accurate answers to their questions about video content.","best_features":["Accurate answers based on video content analysis","Interactive conversations with the AI while watching videos","Customizable and enhanced viewing experience"],"pricing":[]}', 'VideoSage is an AI-powered tool that enhances the video viewing experience by providing accurate answers to questions, offering interactive conversations with the AI, and customizing the viewing experience. Users can ask any question about the video content and receive insightful summaries, insights, and timestamps. The AI ensures credibility and reliability by acknowledging its limitations when faced with unfamiliar queries. VideoSage also provides links to email, YouTube, TikTok, and Instagram channels for user engagement.', NULL, '["Video","AI Assistance","Content Consumption"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f0efd5b0-2898-49ac-ab73-32b33a2fa0bb', '2024-04-12T15:29:31.720Z', 'Videosum', 'https://videosum.ai/', 'images/videosum.webp', 'images/videosum-icon.webp', '{"what":"videosum.ai is an AI-powered tool designed to streamline content creation for online videos. It offers features to transcribe videos, generate subtitles, and create social media posts with ease.","who":"This product is for content creators, social media managers, video editors, and businesses looking to enhance their video content creation process.","best_features":["Effortlessly upload videos through an intuitive interface","Cutting-edge AI technology for accurate video transcriptions","One-click sharing option to multiple social media platforms"],"pricing":[{"plan":"Starter","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"Free","features":["Video transcription","Caption generation","Social media post summaries for up to 2 videos per month"]},{"plan":"Basic","price":9,"billing_cycle":"Monthly","original_price":99,"discount":"","features":["Support for up to 5 videos per month","Additional features for individual content creators and small teams"]},{"plan":"Pro","price":19,"billing_cycle":"Monthly","original_price":179,"discount":"","features":["Support for up to 30 videos per month","Further customization options for growing businesses"]},{"plan":"Business","price":"","billing_cycle":"","original_price":"","discount":"","features":[]}]}', 'videosum.ai is an AI-powered tool that simplifies content creation for online videos by providing video transcription, subtitle generation, and social media post creation. It saves time and effort for content creators and enhances video accessibility and engagement. With different pricing plans catering to various user needs, videosum.ai offers a convenient solution for improving video content and social media presence.', NULL,