INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('12bc59ff-8a87-4311-90be-0079957f6be9', '2024-04-12T15:29:31.720Z', 'Videotap', 'https://videotap.com/', 'images/videotap.webp', 'images/videotap-icon.webp', '{"what":"Video Tap is an AI tool that helps creators and marketers transform videos into various types of content such as blog posts, short clips, summaries, tweets, subtitles, and more.","who":"Video Tap is designed for creators and marketers who want to repurpose their existing video content into multiple formats with minimal effort, enhancing their video workflow exponentially.","best_features":["AI-powered content generation for transforming videos into various formats","Chapters generator for creating meaningful chapters and timestamps for videos","75% cheaper pricing compared to other AI video tools in the market"],"pricing":[{"plan":"Free","price":0,"billing_cycle":"N/A","original_price":0,"discount":"N/A","features":["3 videos free","No credit card required"]}]}', 'Video Tap is an AI tool that transforms videos into different types of content such as blog posts, short clips, summaries, tweets, subtitles, and more. It helps creators and marketers repurpose their video content with ease, generating meaningful chapters and timestamps for improved SEO on platforms like YouTube. With a straightforward two-step process, users can upload their videos and let Video Tap automatically create various content formats. The tool offers cost-effective pricing and seamless integration across different platforms, making it an ideal choice for enhancing video workflows.', NULL, '["AI Tools","Video Editing","SEO"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('a4c1c2c2-818f-4e72-88b3-7305c90749b9', '2024-04-12T15:29:31.720Z', 'Videoticle', 'https://www.videoticle.com/', 'images/videoticle.webp', 'images/videoticle-icon.webp', '{"what":"Videoticle is a tool designed to convert YouTube videos into Medium-style articles, enhancing the user experience by providing a readable format for vlog content primarily focused on dialogue.","who":"Videoticle is for YouTube users who prefer consuming video content in article format, saving time and data usage. It also benefits content creators looking to reach a broader audience by transforming their videos into articles.","best_features":["Conversion of YouTube videos into Medium-style articles","Optimization for vlog content with dialogue","Supplementary plugin for enhanced mobile experience"],"pricing":""}', 'Videoticle is an innovative tool that converts YouTube videos into Medium-style articles, targeting vlog content with dialogue. It simplifies the process by extracting text and snapshots from videos with subtitles, making it easier to consume content in article format. The tool also offers a plugin for mobile devices to enhance the user experience on iOS/iPadOS and Android. Videoticle is beneficial for both content consumers looking for an alternative way to consume video content and content creators seeking to expand their audience reach.', NULL, '["Video Editing","Text-to-Video","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c2b216b1-8675-439f-b72a-6b77b04645d3', '2024-04-12T15:29:31.720Z', 'Videotok', 'https://www.videotok.app/', 'images/videotok.webp', 'images/videotok-icon.webp', '{"what":"Videotok is a tool that uses Artificial Intelligence to create TikTok, Reels, and Shorts-style videos from text input. It automates the video creation process by generating scripts, voices, images, videos, GIFs, captions, effects, and more.","who":"Videotok is designed for content creators, social media influencers, marketers, educators, and anyone looking to create engaging and viral videos quickly and easily.","best_features":["AI Script generation for viral video scripts","AI Voice generation with customizable styles","AI Images, Videos & GIFs generation for high-quality visuals"]}', 'Videotok is a revolutionary tool that leverages Artificial Intelligence to transform text input into captivating TikTok, Reels, and Shorts-style videos. It streamlines the video creation process by automatically generating scripts, voices, images, videos, GIFs, captions, effects, and more. Users can customize their videos further by selecting preferred voices, styles, and content. With features like automatic captions, auto-zoom, background music, and sound effects, Videotok simplifies and enhances the video production experience. Whether for social media, marketing, education, or entertainment purposes, Videotok empowers users to create professional and engaging videos with ease.', NULL, '["AI Tools","Content Creation","Social Media"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('e1116ded-0a5a-4ba1-a402-c5b7951637de', '2024-04-12T15:29:31.720Z', 'VideoToTextAI', 'https://www.videototextai.com/', 'images/videototextai.webp', 'images/videototextai-icon.webp', '{"what":"Video To Text AI is a state-of-the-art Artificial Intelligence-based transcription service that specializes in providing rapid and accurate transcription of audio and video files.","who":"This product is designed for content creators, professionals, and individuals who require high-quality transcription services for their audio and video files.","best_features":["Efficient and accurate transcription of audio and video files","Easy-to-use interface for seamless transcription process","Cost-effective solution for a broad range of users"],"pricing":[{"plan":"Transcribe","price":9.99,"billing_cycle":"per month","original_price":14.99,"discount":"33% off","features":["Fast and accurate transcription","Support for YouTube video transcription","User-specific features upon logging in"]}]}', 'Video To Text AI is an AI-powered transcription service that efficiently converts audio and video files into text. It is designed to be user-friendly and cost-effective, catering to the needs of content creators, professionals, and individuals seeking accurate transcription services. Users can upload audio files or provide YouTube URLs for transcription, with the AI completing the process swiftly. The service also offers additional user-specific features and support. Video To Text AI prioritizes accuracy and quality in its transcriptions, making it a valuable tool for simplifying complex transcription tasks.', NULL, '["Transcription","Artificial Intelligence","Audio Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('4c33a7f9-f60e-4025-8194-8ba9e94042b3', '2024-04-12T15:29:31.721Z', 'Vidiofy', 'https://www.vidiofy.ai/', 'images/vidiofy.webp', 'images/vidiofy-icon', '{"what":"Vidiofy is a generative AI tool designed to convert text-based content into mobile-first, vertical, short-form videos for social media platforms. It simplifies the process of creating engaging reel-style videos from written articles.","who":"Vidiofy is for brands and publishers looking to repurpose their written content into visually appealing videos for social media platforms. It is also beneficial for marketing professionals and educators seeking to enhance their video creation and viewership.","best_features":["Effortlessly convert text-based content into captivating reel-style videos","Customizable branded templates for visually appealing videos","Intuitive interface for quick and easy video creation"]}', 'Vidiofy is a generative AI tool that converts text-based content into mobile-first, vertical, short-form videos for social media platforms. It offers customizable branded templates, an intuitive interface, and the ability to reach new audiences online. Trusted by well-known brands and publishers worldwide, Vidiofy simplifies the process of creating engaging videos from written articles.', NULL, '["AI Tools","Video Generation"]');
