INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('28beeec5-bc78-40d0-a8d0-e19d653afacc', '2024-04-12T15:29:31.733Z', 'Voicebox by Meta', 'https://ai.facebook.com/blog/voicebox-generative-ai-model-speech/', 'images/voicebox-by-meta.webp', 'images/voicebox-by-meta-icon.webp', '{"what":"Voicebox by Meta is a generative AI model for speech that can generalize to tasks it was not specifically trained for with state-of-the-art performance. It uses a new approach called Flow Matching to learn highly non-deterministic mapping between text and speech.","who":"Voicebox is designed for researchers, developers, and AI enthusiasts looking to advance the state of the art in generative AI for speech. It can also be used by companies working on virtual assistants, speech recognition, and audio editing.","best_features":["Ability to generalize to tasks it was not specifically trained for","Can be trained on diverse, unstructured data without requiring carefully labeled inputs","Outperforms existing state-of-the-art speech models on word error rate and audio similarity metrics"],"pricing":[]}', 'Voicebox by Meta is a groundbreaking generative AI model for speech that can synthesize high-quality audio clips in multiple languages, perform noise removal, content editing, style conversion, and diverse sample generation. It outperforms existing models on word error rate and audio similarity metrics, making it a valuable tool for various applications in speech synthesis and editing.', NULL, '["Speech-to-text","Audio Editing","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('8961c9aa-3eef-436d-82ea-75430d369f1c', '2024-04-12T15:29:31.733Z', 'Voicedraft', 'https://voicedraft.io/', 'images/voicedraft.webp', 'images/voicedraft-icon.webp', '{"what":"VoiceDraft.io is an AI-based tool that enables video creators to generate realistic voiceover speech demos for their projects using text-to-speech technology.","who":"VoiceDraft.io is designed for video creators who want to save time and costs by creating high-quality voiceovers for their projects without the need for external voice actors.","best_features":["AI-driven text-to-speech technology for realistic voiceovers","Simple and straightforward user experience for easy navigation","Instant voiceover generation and preview for quick project turnaround"],"pricing":[]}', 'VoiceDraft.io is an AI-driven tool that allows video creators to easily generate high-quality voiceover speech demos for their projects. By utilizing text-to-speech technology, users can select a voice, input their text, and instantly download the generated voiceover. The tool aims to address common challenges faced by video creators, such as finding the right voice and reducing costs associated with hiring voiceover talents. With fast turnaround and affordability, VoiceDraft.io offers a cost-effective and efficient solution for incorporating voiceovers into video projects.', NULL, '["Video Editing","Text-to-Speech"]');
