INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('ef4f742b-6d03-4cda-beae-e07852b9b452', '2024-04-12T15:29:31.780Z', 'Youtube Title Generator', 'https://reignewz.com/youtube-title-generator/', 'images/youtube-title-generator.webp', 'images/youtube-title-generator-icon.webp', '{"what":"The YouTube Title Generator by Reignewz is a powerful tool designed to help content creators and video marketers generate attention-grabbing and click-worthy titles for their YouTube videos.","who":"This product is for content creators and video marketers who want to increase their video''s click-through rates, boost viewer engagement, and enhance its discoverability.","best_features":["Uses artificial intelligence (AI) and data analysis to generate creative, catchy, and unique titles tailored to your content.","Allows input of keywords or custom prompts for personalized title generation.","Templates created by studying successful YouTube videos combined with AI technology and clever prompts."],"pricing":[]}', 'The YouTube Title Generator by Reignewz is a powerful tool designed to help content creators and video marketers generate attention-grabbing and click-worthy titles for their YouTube videos. With the use of AI and data analysis, this tool can increase click-through rates, boost viewer engagement, and enhance video discoverability by generating creative and unique titles tailored to the content.', NULL, '["Video Editing","AI Tools"]');
