INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c269542d-a225-4441-8665-eee9942c2caa', '2024-04-12T15:29:31.781Z', 'YouTube to Chatbot', 'https://youtube-to-chatbot.streamlit.app/', 'images/youtube-to-chatbot.webp', 'images/youtube-to-chatbot-icon.webp', '{"what":"The YouTube to Chatbot tool by Streamlit is a software solution that converts YouTube videos into interactive chatbots using natural language processing and machine learning algorithms.","who":"This tool is designed for content creators, developers, researchers, and anyone looking to repurpose YouTube videos for applications such as customer support, education, or entertainment.","best_features":["Converts YouTube videos into text-based chatbots for interactive conversations.","Utilizes advanced NLP techniques to extract meaningful dialogue exchanges.","Supports easy integration with existing chatbot platforms or custom frameworks."],"pricing":""}', 'The YouTube to Chatbot tool offered by Streamlit converts YouTube videos into interactive chatbots by extracting dialogues, captions, and subtitles and transforming them into structured conversational agents. It enables dynamic interactions within video content, enhances accessibility, and engages users in a more interactive manner. The tool maintains the essence and context of the original video while creating a conversational experience. Users can deploy the chatbots for various applications such as customer support, education, or entertainment.', NULL, '["Chat Solutions","Content Creation","AI Agents"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d31893f4-2f31-4973-895d-6ef3e155e080', '2024-04-12T15:29:31.781Z', 'YouTube Transcripts', 'https://www.tubetranscripts.com/', 'images/youtube-transcripts.webp', 'images/youtube-transcripts-icon.webp', '{"what":"YouTube Transcripts is an AI-powered tool that enhances the accessibility and search engine optimization (SEO) of YouTube videos by providing accurate video transcriptions.","who":"This product is for YouTube creators and developers who want to improve their video SEO, accessibility, and engagement on the platform.","best_features":["AI-powered accurate video transcriptions for improved SEO","Customizable transcripts with niche keywords and custom mappings","Editing feature to refine transcripts for human transcription quality"]}', 'YouTube Transcripts is an AI-powered tool that enhances the accessibility and search engine optimization (SEO) of YouTube videos by providing accurate video transcriptions. It integrates directly with YouTube Studio, allowing users to create engaging transcripts that boost visibility on the platform. The tool offers customizable transcripts with niche keywords and custom mappings, as well as an editing feature to refine transcripts for human transcription quality.', NULL, '["Video Editing","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('27c32acc-ebd5-40f4-abcc-3f7a91137bbb', '2024-04-12T15:29:31.781Z', 'YT Copycat', 'https://ytcopycat.com/', 'images/yt-copycat.webp', 'images/yt-copycat-icon.webp', '{"what":"YT Copycat is an AI-powered tool designed to boost productivity and create high-quality content from YouTube videos. It helps users generate SEO optimized blog articles, tweets, and newsletters in seconds, making it ideal for marketers, bloggers, influencers, and anyone looking to save time while creating engaging content.","who":"YT Copycat is for marketers, bloggers, influencers, and individuals seeking to streamline their content creation process. It caters to those who want to repurpose YouTube videos into various formats and reach a wider audience through different content channels.","best_features":["AI-powered content generation from YouTube videos","Ability to create engaging Twitter threads from videos","Automatic publishing of blog articles for newly released videos"],"pricing":[{"plan":"7-days free trial","price":0,"billing_cycle":"monthly","original_price":0,"discount":"100% off","features":["Unlimited content generation","Access to productivity resources","Automated blog updates"]}]}', 'YT Copycat is a powerful AI tool that transforms YouTube videos into high-quality content such as blog articles, tweets, and newsletters. It offers features like AI-powered content generation, Twitter thread creation, and automatic blog publishing. Ideal for marketers, bloggers, and influencers, YT Copycat streamlines the content creation process and helps users reach a wider audience through repurposed content.', NULL, '["AI Tools","Marketing","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('21efb83c-42f2-4d1a-a8ac-0f3fa5b824a0', '2024-04-12T15:29:31.781Z', 'YT Generator', 'https://ytgenerator.com/', 'images/yt-generator.webp', 'images/yt-generator-icon.webp', '{"what":"YT Generator is a digital tool designed to streamline and optimize the creation of YouTube metadata, such as descriptions, titles, and tags. It helps users generate engaging and search engine optimized descriptions for YouTube videos.","who":"YT Generator is for content creators, YouTubers, digital marketers, and anyone looking to enhance the visibility and engagement of their YouTube videos.","best_features":["Effortless Optimization: Boost your video''s visibility with SEO-friendly descriptions.","Creative Amplification: Add a creative flair to your descriptions to resonate with viewers.","Time Saving Convenience: Save time with instant, ready-to-use titles, tags, and descriptions."],"pricing":[]}', 'YT Generator is a digital tool that simplifies the process of creating YouTube metadata by generating engaging and SEO-friendly descriptions, titles, and tags. It offers a convenient way for content creators to enhance the visibility and engagement of their YouTube videos, saving time and boosting efficiency in the content production process.', NULL, '["SEO","Video Editing"]');
