INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('6379e33c-a254-4d03-b537-f01c251452ac', '2024-04-12T15:29:31.788Z', 'ZippyNotes', 'https://zippynotes.co/', 'images/zippynotes.webp', 'images/zippynotes-icon.webp', '{"what":"ZippyNotes is an AI-powered tool designed to enhance and streamline the learning and note-taking process on YouTube by allowing users to take timestamped notes directly while watching videos.","who":"ZippyNotes is for students, researchers, content creators, and anyone who wants to efficiently take notes while watching YouTube videos.","best_features":["Seamless integration with Chrome Extension for easy note-taking","Automatic generation of summarized notes for educational videos","Efficient organization and retrieval of notes for improved learning"],"pricing":[]}', 'ZippyNotes is an AI-powered tool that enhances the learning and note-taking process on YouTube. It allows users to take timestamped notes while watching videos, generate summarized notes automatically, and organize notes effectively. The tool is designed to facilitate online education, video-based research, and entertainment note-taking.', NULL, '["Note-taking","Video Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f90323cd-996d-4282-b2fd-fb6b5353cd20', '2024-04-12T15:29:31.788Z', 'Zipscore', 'https://www.zipscore.ai/', 'images/zipscore.webp', 'images/zipscore-icon.webp', '{"what":"Zipscore.ai is an AI co-pilot tool for market analysis and location planning, powered by AI technology and extensive demographic data.","who":"This tool is designed for businesses looking to efficiently identify target market areas for their products or services.","best_features":["Comprehensive census data and ZIP Code maps for market analysis","Real-time collaboration with team members through shared polygons or paths","Fast filtering capabilities to target specific demographics in chosen ZIP codes"],"pricing":[]}', 'Zipscore.ai is an AI co-pilot tool for market analysis and location planning, offering comprehensive census data and ZIP Code maps for exploring various geographical details. It enables real-time collaboration among team members, fast filtering capabilities for targeting specific demographics, and insights for advertising platforms customization. The tool is powered by AI technology and recent data from 75,000 US locations.', NULL, '["Market Analysis","Location Planning"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('324e204b-7dbd-4df6-9ac6-fdd9b8495f31', '2024-04-12T15:29:31.788Z', 'ZipWP', 'https://zipwp.com/', 'images/zipwp.webp', 'images/zipwp-icon.webp', '{"what":"ZipWP is an AI-powered website builder tool that creates professional WordPress websites in just 60 seconds based on user descriptions. It aims to simplify and streamline the website creation process for both web design professionals and entrepreneurs.","who":"ZipWP is designed for web design professionals looking to serve more clients efficiently and entrepreneurs or business owners who want to create mobile-responsive websites without any technical expertise.","best_features":["AI-powered website creation in 60 seconds","Professional design, copy, and visuals tailored to user descriptions","Customization options and flexibility with WordPress integration"],"pricing":[{"plan":"Free Plan","price":0,"billing_cycle":"Monthly","original_price":0,"discount":"No discount","features":["AI-generated content and images","Customizable templates","Seamless hosting"]}]}', 'ZipWP AI Website Builder is a tool that uses artificial intelligence to generate WordPress websites quickly and effortlessly. Users simply describe their idea, and the AI system creates a website draft with design, images, and persuasive copy tailored to that description. The tool focuses on user-friendliness, customization, and professional-looking websites ready in a short time frame. ZipWP is suitable for web design professionals and entrepreneurs alike, offering a seamless website creation experience without the need for technical expertise.', NULL, '["AI Tools","WordPress","Website Builder"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('37b2c886-2ce5-47df-8aa9-e74795033115', '2024-04-12T15:29:31.788Z', 'Zipx', 'https://www.zipx.ai/', 'images/zipx.webp', 'images/zipx-icon.webp', '{"what":"Zipx is an AI Graphic Designer tool designed to assist businesses in editing product images without the need for a traditional graphic designer. It offers cost-effective and efficient solutions for creating visually appealing product listings.","who":"Zipx is for businesses looking to reduce costs associated with graphic design, improve productivity in creating product listings, and overcome language barriers in e-commerce platforms. It is suitable for all platforms and users seeking an alternative to manual image editing processes.","best_features":["Cost-effective solution operating at 10% of the salary of a human graphic designer while providing 10 times the productivity.","Ease of use with the ability to generate professional-looking product listings by simply uploading one image.","Language versatility through generative methods for native-sounding listings and access to a community for sharing experiences and methods."],"pricing":[]}', 'Zipx is an AI Graphic Designer tool that helps businesses create professional product listings without the need for a traditional graphic designer. It offers cost-effective solutions, ease of use, cross-platform compatibility, and language versatility. Users can join a community for sharing experiences and methods, with incentives available. Zipx aims to streamline the image editing process for businesses seeking visually appealing product listings.', NULL, '["E-commerce","Image Editing"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d6274f42-d941-40d9-8928-cd31ad0f62ec', '2024-04-12T15:29:31.788Z', 'Zivy Listens', 'https://zivylabs.com/', 'images/zivy-listens.webp', 'images/zivy-listens-icon.webp', '{"what":"Zivy Listen is an AI tool that converts written articles into concise and engaging audio podcasts, designed for on-the-go consumption.","who":"Zivy Listen is for busy individuals looking to save time and stay informed by transforming long reads into short, impactful listens.","best_features":["AI & GPT integration for extracting key insights from articles","Customized listening experience with the ability to select specific sections to listen to","Note-taking functionality for highlighting sections and sharing papers among friends"],"pricing":[]}', 'Zivy Listen is an AI tool that converts written articles into concise and engaging audio podcasts, designed for mobile use. It supports various formats and allows users to extract key insights from academic papers, even before reading. Users can customize their listening experience, take notes, and share content with friends. With a user-friendly interface and realistic voices, Zivy Listen enhances productivity and reading habits for busy individuals.', NULL, '["AI Tools","Text-to-Speech","Podcasting"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('d464fdfb-0e38-471c-9a80-4d6eceb55d36', '2024-04-12T15:29:31.788Z', 'Zixflow', 'https://zixflow.com/', 'images/zixflow.webp', 'images/zixflow-icon.webp', '{"what":"Zixflow is an all-in-one AI-driven platform designed to enhance customer management, engagement, and retention through customizable CRM, workflow automation, and omnichannel communication.","who":"Zixflow is ideal for sales, marketing, and support teams across various industries looking to streamline their customer interactions and automate repetitive tasks.","best_features":["Omnichannel automation for effective outreach","Lead capturing with smart forms","Email finder tool for discovering and verifying prospect emails"],"pricing":[]}', 'Zixflow is a versatile AI-driven platform that offers CRM, workflow automation, and customer engagement tools. It enables users to customize their processes through email, SMS, and WhatsApp channels. With features like omnichannel automation, lead capturing, and email validation, Zixflow simplifies communication and task management. The platform integrates with various applications and includes a powerful AI engine for generating email content. Designed to benefit sales, marketing, and support teams, Zixflow streamlines workflows and enhances productivity.', NULL, '["CRM","Marketing","Automation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('c6e12175-409b-4a1d-9646-08c7209ff2c3', '2024-04-12T15:29:31.789Z', 'Zmo Anime Generator', 'https://www.zmo.ai/ai-anime-generator/', 'images/zmo-anime-generator.webp', 'images/zmo-anime-generator-icon.webp', '{"what":"The Zmo Anime Generator is an AI-powered tool that allows users to create stunning anime characters and images using simple prompts or by converting text to anime. It offers precision control and unparalleled detail, making it perfect for artists and designers looking to unleash their creativity.","who":"This product is for artists, designers, anime enthusiasts, and anyone looking to create high-quality anime-style art quickly and easily.","best_features":["Wide selection of 50+ distinct anime AI filters for customization","Facial enhancement capabilities for lifelike and natural-looking images","Ability to convert photos into AI-generated anime images"],"pricing":[{"plan":"Free","price":0,"billing_cycle":"One-time","original_price":0,"discount":"N/A","features":["Access to basic features","Earn credits by sharing or creating an account","Some advanced features may require credits"]}]}', 'The Zmo Anime Generator is an AI tool that simplifies the process of creating professional-looking anime illustrations. Users can choose from a wide selection of filters, enhance facial features, and even convert photos into AI-generated anime images. The tool is free to use with the option to earn credits for advanced features.', NULL, '["AI Tools","Image Editing","Anime"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('edbf1fb2-7ddf-47ac-83d5-f272416ea58e', '2024-04-12T15:29:31.789Z', 'Zodiac-Chat', 'https://zodiac-chat.com/', 'images/zodiac-chat.webp', 'images/zodiac-chat-icon', '{"what":"Zodiac-Chat.com is a free online astrology tool designed to provide users with insights into their zodiac signs and horoscopes using advanced AI technology.","who":"This product is for astrology enthusiasts, curious individuals looking to learn more about their zodiac signs, and anyone interested in personalized astrology readings and predictions.","best_features":["Personalized readings and predictions based on user input or astrological profile.","In-depth zodiac analyses covering love, careers, and personal growth.","Premium features available for more detailed and comprehensive zodiac reports."],"pricing":[{"plan":"Free Plan","price":0,"billing_cycle":"N/A","original_price":0,"discount":"N/A","features":["Personalized readings and predictions","In-depth zodiac analyses","Access to basic features"]},{"plan":"Premium Plan","price":5,"billing_cycle":"Monthly","original_price":10,"discount":"50% off","features":["Detailed zodiac reports","Customizable options","Unlimited characters"]}]}', 'Zodiac-Chat.com is a free online astrology tool that offers personalized readings, predictions, and in-depth zodiac analyses based on user input or astrological profiles. It leverages advanced AI technology to provide insights into love, careers, and personal growth. Premium features are available for more detailed zodiac reports tailored to specific requirements.', NULL, '["Astrology","AI Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('2ee9ae17-a083-446a-a0c0-699084fb6046', '2024-04-12T15:29:31.789Z', 'Zomory', 'https://zomory.com', 'images/zomory.webp', 'images/zomory-icon.webp', '{"what":"Zomory is an AI-powered search engine that helps teams find information quickly and easily by automatically syncing with workspace and using natural language processing to provide up-to-date results with context.","who":"Zomory is designed for teams and individuals who want to save time and improve productivity by easily accessing information in their workspace without the need for manual searching or copying.","best_features":["Automatic syncing with workspace for up-to-date results","Natural language processing for accurate search results","Integration with Slack for easy access from anywhere"],"pricing":[]}', 'Zomory is an AI-powered search engine that eliminates the need for manual searching and copying by automatically syncing with workspace and using natural language processing to provide up-to-date results with context. It saves time, improves productivity, and can be integrated with Slack for easy access from anywhere.', NULL, '["Search Engines","Productivity Tools"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('f9f89324-6b96-49fc-afa7-3c827e9b0a0b', '2024-04-12T15:29:31.789Z', 'Zoo by Replicate', 'https://zoo.replicate.dev/', 'images/zoo-by-replicate.webp', 'images/zoo-by-replicate-icon.webp', '{"what":"Zoo by Replicate is a playground for generating photo-realistic images from text inputs using various text-to-image AI models.","who":"Zoo is for researchers and developers exploring text-to-image AI models and their applications.","best_features":["Utilizes various text-to-image AI models for generating photo-realistic images","Incorporates AI-FOREVERKANDINSKY-2 and OpenAI''s DALL-E for creating realistic images from text descriptions","Open-source repository on GitHub for accessibility and collaboration"],"pricing":""}', 'Zoo by Replicate is a tool that allows users to generate photo-realistic images from text inputs using advanced text-to-image AI models. It incorporates models like STABILITY-AISTABLE-DIFFUSION and AI-FOREVERKANDINSKY-2 to create images based on natural language descriptions. Zoo is an open-source project on GitHub, making it accessible for researchers and developers interested in exploring the latest advancements in computer vision AI.', NULL, '["Art Generation","Image Generation"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('09822915-5d74-4881-b55f-fb490916adaa', '2024-04-12T15:29:31.789Z', 'Zoviz', 'https://zoviz.com/', 'images/zoviz.webp', 'images/zoviz-icon.webp', '{"what":"Zoviz is a platform that automates branding components through AI technology, streamlining the creation of unique logos and comprehensive brand kits.","who":"Zoviz is designed for individuals, startups, enterprises, and freelance designers looking to create high-quality logos and brand identities efficiently.","best_features":["AI-powered logo suggestions tailored to user preferences","Customizable design elements for creating unique logos","Instant download of high-quality logo files in various formats"],"pricing":[{"plan":"Full Package","price":49.99,"billing_cycle":"one-time payment","original_price":69.99,"discount":"29% off","features":["Access to 30+ high-res logo file types","Social media profile customization","Email signature templates"]},{"plan":"Basic Package","price":19.99,"billing_cycle":"one-time payment","original_price":29.99,"discount":"33% off","features":["AI-powered logo maker","Customizable design elements","Instant download of logo files"]}]}', 'Zoviz is a platform that automates branding components through AI technology, enabling users to create unique logos and comprehensive brand kits efficiently. Users input their brand name and tagline, and the AI engine generates a tailored logo and brand kit. The platform offers customizable design elements, high-quality outputs, and a brand book for consistent branding. Zoviz caters to individuals, startups, enterprises, and freelance designers, providing a one-stop solution for logo creation and brand identity development.', NULL, '["AI Technology","Logo Design","Brand Identity"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('84543e5a-8ffa-471d-8bdc-5248544c9ef5', '2024-04-12T15:29:31.789Z', 'ZOYO', 'https://www.zoyo.ai/', 'images/zoyo.webp', 'images/zoyo-icon.webp', '{"what":"ZOYO is an AI-powered real estate platform that offers a range of tools for professionals in the real estate and associated industries. It provides services such as interior and exterior design, image enhancement, virtual staging, and ultra-realistic rendering of real estate images.","who":"ZOYO is designed for realtors, brokerages, homeowners, interior designers, builders, contractors, and architects who are looking to enhance their property listings, revamp interior and exterior spaces, and save time and cost on traditional design services.","best_features":["High-quality renderings using proprietary ULTRA image model","Ultra-realistic rendering of real estate images","Quick generation of design ideas for interior and exterior spaces"],"pricing":[]}', 'ZOYO is an AI-powered real estate platform that offers a variety of tools and services for professionals in the real estate industry. It allows users to enhance and alter images, generate high-quality renderings, and create ultra-realistic visuals for property listings. ZOYO is a valuable resource for real estate professionals looking to improve their listings, save time on design services, and attract potential buyers with visually appealing content.', NULL, '["AI Tools","Image Editing","Design"]');
INSERT INTO products (id,created_at,name,url,website_image_url,icon_url,meta,description,category_id,subcategory) VALUES ('b360d303-c834-4cb2-ba56-5b4fa7a38d2f', '2024-04-12T15:29:31.790Z', 'Zuckisreal', 'https://www.twitch.tv/zuckisreal', 'images/zuckisreal.webp', 'images/zuckisreal-icon.webp', '{"what":"ZuckIsReal is an interactive live streaming tool on Twitch that allows users to chat with an AI parody version of Mark Zuckerberg. The tool creates an artificial intelligence clone of Zuckerberg which users can interact with in real time.","who":"ZuckIsReal is for Twitch users who are interested in engaging with a parody AI version of Mark Zuckerberg and enjoy interactive live streaming experiences.","best_features":["Real-time interaction with an AI parody version of Mark Zuckerberg","Privacy-focused approach with options to manage cookies and personalized content","Engaging experience within the Just Chatting category on Twitch"],"pricing":""}', 'ZuckIsReal is an interactive live streaming tool on Twitch that allows users to chat with an AI parody version of Mark Zuckerberg. Users can engage with the AI clone by typing commands in the chat. The tool emphasizes privacy and offers options to manage cookies and personalized content. ZuckIsReal falls under the Just Chatting category on Twitch and provides users with a unique and engaging experience.', NULL, '["AI","Live Streaming","Interactive Chat"]');
